public void AcctApproval_chk()
{
    if(SKS_EFT_Setup_APAcctApproval.value() || SKS_EFT_Setup_APBankAcctApproval.value())
    {
        SKS_EFT_Setup_APAccountApprGroup.enabled(true);
        SKS_EFT_Setup_APApproverChangeInfo.enabled(true);
    }
    else
    {
        SKS_EFT_Setup_APAccountApprGroup.enabled(false);
        SKS_EFT_Setup_APApproverChangeInfo.enabled(false);
        SKS_EFT_Setup.APApproverChangeInfo = NoYes::No;
    }
}

public void activate(boolean _active)
{
    SKS_EB_FileStorage sks_EB_FileStorage;
    SKS_BF_Connection newConnection = SKS_BF_Connection::constuct();

    container   bfFileLocationList;
    int i;

    element.AcctApproval_chk();
    element.apAdvProcessing_chk();

    SKS_EFT_Setup_APArchive.archivecheckbox_chk();

    this.isEmailEnabled();
    // SKS_FDD-10590 -->
    SKS_EFT_Setup_APCreateSendCompany.enabled(SKS_EFT_Setup.APCreateSendLeter == NoYes::Yes && SKS_PP_Setup.modMCFAPDD );
    if(!SKS_EFT_Setup.APCreateSendCompany)
    {
        SKS_EFT_Setup.APCreateSendCompany = curExt();
    }

    SKS_EFT_Setup_APPeriodsRet.enabled(SKS_EFT_Setup.APPurgeFiles);
    // SKS_FDD-10590 <--

    super(_active);
}

void apAdvProcessing_chk()
{
    if(SKS_EFT_Setup.APAutoCreate == NoYes::Yes)
    {
        SKS_EFT_Setup_APAdvProcessing.enabled(false);
    }
    else
    {
        SKS_EFT_Setup_APAdvProcessing.enabled(true);
    }

    if(SKS_EFT_Setup.APAdvProcessing == NoYes::Yes)
    {
        SKS_EFT_Setup_APAPOComm.enabled(true);
        SKS_EFT_Setup_APAPOPreNotesOne.enabled(true);
    }
    else
    {
        SKS_EFT_Setup_APAPOComm.enabled(false);
        SKS_EFT_Setup_APAPOPreNotesOne.enabled(false);
    }
}

public boolean canClose()
{
    boolean ret;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if(SKS_EFT_Setup.APAcctApproval == NoYes::Yes && !SKS_EFT_Setup_APAccountApprGroup.valueStr())
    {
        Box::warning("@SKS253","@SKS225"); //required field

        SKS_EFT_Setup_APAccountApprGroup.setFocus();

        return false;
    }

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFT_Setup_APBankFabricFileLocation.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFT_Setup_APFileName.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APFileName.setFocus();

            return false;
        }

        if(!SKS_EFT_Setup_APFilePath.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APFilePath.setFocus();

            return false;
        }

    if(SKS_EFT_Setup_APArchive.value() && !SKS_EFT_Setup_APPathArchive.valueStr() && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
    {
        Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APPathArchive.setFocus();

            return false;
        }
    }

    ret = super();

    return ret;
}

