private void enableEmailTab(boolean _visibleValue)
{
    Email.visible(_visibleValue);
    Template.visible(_visibleValue);
    ReplyTo.visible(_visibleValue);
    Company.visible(_visibleValue);
    APRemitAttachmentOptions.visible(_visibleValue);
    SKS_EFT_Setup_IHB_Remit.visible(_visibleValue);
    RemitAttachDetail.visible(_visibleValue);
    APSRS1RemitAttachmentDetail.visible(_visibleValue);
    APSRS2RemitAttachmentDetail.visible(_visibleValue);
    BankAccountAttachDetail.visible(_visibleValue);
    APSRSRemitAttachmentDetailV2.visible(_visibleValue);

    if (_visibleValue)
    {
        RemitAttachDetail.enabled(SKS_EFT_Setup.APTemplateAttachment);
        APSRS1RemitAttachmentDetail.enabled(SKS_EFT_Setup.APSRS1Attachment);
        APSRS2RemitAttachmentDetail.enabled(SKS_EFT_Setup.APSRS2Attachment);
        BankAccountAttachDetail.enabled(SKS_EFT_Setup.APTemplateAttachment);
        APSRSRemitAttachmentDetailV2.enabled(SKS_EFT_Setup.APSRS1Attachment || SKS_EFT_Setup.APSRS2Attachment);
    }
}

