public void EnableDisableHideDueDateOption()
{
    boolean V2Assignedremit1 = SKS_EFT_Setup.APSRS1EmailSSRS == 'SKS_APR_RemitAttachControllerV2' ? true : false;
    boolean V2Assignedremit2 = SKS_EFT_Setup.APSRS2EmailSSRS == 'SKS_APR_RemitAttachControllerV2' ? true : false;
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, APHideDueDateAtt1)).allowEdit(V2Assignedremit1);
    SKS_EFT_Setup.APHideDueDateAtt1 = V2Assignedremit1 == false ? false : SKS_EFT_Setup.APHideDueDateAtt1;
    SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, APHideDueDateAtt2)).allowEdit(V2Assignedremit2);
    SKS_EFT_Setup.APHideDueDateAtt2 = V2Assignedremit2 == false ? false : SKS_EFT_Setup.APHideDueDateAtt2;
}

public boolean validateWrite()
{
    boolean ret;
    VendPaymModeTable   lVendPaymModeTable;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;


    if(SKS_EFT_Setup.APAcctApproval == NoYes::Yes && !SKS_EFT_Setup_APAccountApprGroup.valueStr())
    {
        Box::warning("@SKS253","@SKS225"); //required field

        SKS_EFT_Setup_APAccountApprGroup.setFocus();

        return false;
    }

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFT_Setup_APBankFabricFileLocation.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFT_Setup_APFileName.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APFileName.setFocus();

            return false;
        }

        if(!SKS_EFT_Setup_APFilePath.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APFilePath.setFocus();

            return false;
        }

    if(SKS_EFT_Setup_APArchive.value() && !SKS_EFT_Setup_APPathArchive.valueStr() && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
    {
        Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_APPathArchive.setFocus();

            return false;
        }
    }

    if(SKS_EFT_Setup.APCommID == '')
    {
        warning("@SKS253","@SKS225"); //required field

        SKS_EFT_Setup_CommID.setFocus();

        return false;
    }

    if(SKS_EFT_Setup.AllowPostedPmtJournal == NoYes::Yes)
    {
        if(SKS_EFT_Setup.RevPaymMode == '')
        {
            warning("@SKS253","@SKS3012"); //required field

            SKS_EFT_Setup_RevPaymMode.setfocus();

            return false;
        }

        if(SKS_EFT_Setup.AutoPostJournal == NoYes::Yes)
        {
            select firstonly Paymstatus from lVendPaymModeTable where lVendPaymModeTable.PaymMode == SKS_EFT_Setup.RevPaymMode;

            if(lVendPaymModeTable.PaymStatus != CustVendPaymStatus::None)
            {
                error("@SKS3014");

                return false;
            }
        }
    }

    ret = super();

    if(ret)
    {
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Setup.APEMTemplateNoAttach, SKS_EFT_Setup.APEMTemplateLanguage) && ret;
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Setup.APEMTemplateAttach, SKS_EFT_Setup.APEMTemplateLanguage) && ret;
    }

    return ret;
    }

