/// <summary>
///This methid used to validate the given company values
/// </summary>
/// <returns></returns>
public boolean validate()
{
    DataArea    dataArea;
    container   companyList = SKS_EB_VirtualCompany::getEFT_APMultiCompanies();
    int         value;

    boolean ret;
    ret = super();

    if(SKS_EFT_Setup.APCreateSendCompany)
    {
        value  = conFind(companyList, SKS_EFT_Setup.APCreateSendCompany);
        select firstonly Id from dataArea where dataArea.id == SKS_EFT_Setup.APCreateSendCompany;
        if(!dataArea.id || !value)
        {
            ret = checkfailed("@SKS:SKGPayCompExists");
        }
    }
    return ret;
}

