public void modified()
{
    SKS_EFT_VendorPaym sks_eft_VendorPaym;
    SKS_EFT_FileFormat  sks_eft_fileformat;
    SKS_EFT_FileFormatPaymSpec  sks_eft_fileformatpaymspec;
    VendBankAccount vendbankaccount;
    boolean showMessage = false;
    ;

    ttsbegin;

    if(!SKS_EFT_Setup.orig().APReqPreNote && SKS_EFT_Setup.APReqPreNote)
    {
        while select FormatId, PaymSpec, VendCompany, VendAccount, VendBankAccountId from sks_eft_VendorPaym
        join prenote, formatid from sks_eft_fileformat
        where sks_eft_fileformat.FormatId == sks_eft_VendorPaym.FormatId
            && sks_eft_fileformat.PreNote == NoYes::Yes
        join prenote, formatid, paymspec from sks_eft_fileformatpaymspec
        where  sks_eft_fileformatpaymspec.FormatId == sks_eft_fileformat.FormatId
            && sks_eft_fileformatpaymspec.PaymSpec == sks_eft_vendorpaym.PaymSpec
            && sks_eft_fileformatpaymspec.PreNote == NoYes::Yes
        {
            changeCompany(sks_eft_VendorPaym.VendCompany)
            {
                vendBankAccount = null;

                select firstonly forupdate vendbankaccount where vendbankaccount.AccountID == sks_eft_VendorPaym.VendBankAccountId
                    && vendbankaccount.VendAccount == sks_eft_VendorPaym.VendAccount
                    && vendbankaccount.SKS_EFT_PreNoteStatus == SKS_EB_PreNoteStatus::NotApplicable;

                if(vendbankaccount)
                {
                    vendBankAccount.SKS_EFT_PreNoteStatus = SKS_EB_PreNoteStatus::New;
                    vendBankAccount.SKS_EFT_PreNoteDate = dateNull();
                    vendBankAccount.update();
                    showMessage = true;
                }
            }
        }
        if(showMessage)
        {
            info("@SKS1482");
        }
    }
    super();

    ttscommit;
}

