public void lookup()
{
    SKS_BF_TmpLookupTable TmpLookupTable;

    Query query = new Query();

    QueryBuildDataSource qbds;

    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup;

    TmpLookupTable = SKS_BF_TmpLookupTable::construct(SKS_BF_FileLocCategory::APEFT);

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),this);

    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));

    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));

    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable,Name));

    sysTableLookup.parmTmpBuffer(TmpLookupTable);

    sysTableLookup.performFormLookup();
    //super();
}

void archivecheckbox_chk()
{
    //if archive checkbox is clicked enable archive path, otherwise disable the path
    if( SKS_EFT_Setup_APArchive.value())
    {
        SKS_EFT_Setup_APPathArchive.enabled(true);
    }
    else
    {
        SKS_EFT_Setup_APPathArchive.enabled(false);
    }
}

public void clicked()
{
    super();

    this.archivecheckbox_chk();
}

void clicked()
{
    SKS_PP_Sys cls_SKS_PP_Sys = new SKS_PP_Sys();

    super();

    cls_SKS_PP_Sys.CallModalForm( element, formstr(SysEmailTable));
}

void clicked()
{
    Dialog  dialog;
    DialogField dialogField;
    DialogField dialogField2;
    ;

    dialog = new Dialog("@SKS1617");
    //default to .csv files
    dialogfield = dialog.addField(extendedTypeStr(Email), "@SKS1618");
    dialogfield2 = dialog.addField(extendedTypeStr(Email), "@SYS53122");
    if (dialog.run())
    {
        if ((dialogfield.value() != '') && (dialogfield2.value() != ''))
        {
            SKS_EFT_SendMail::mailServerTest(dialogfield.value(),dialogfield2.value());
        }
        else
        {
            box::info("@SKS1527");
        }
    }
    else
    {
        return; // if they just cancelled the dialog, then no error.
    }
}

/// <summary>
///
/// </summary>
public void clicked()
{
    Args args;

    FormRun formRun;

    ;

    args = new Args();
    Args.record(SKS_EFT_Setup);
    args.name(formstr(SKS_EFT_EmailPurposeSetup));

    formRun=new FormRun(args);

    formRun.run();

    formRun.wait();

}

/// <summary>
/// defaults values for the bank account.
/// </summary>
/// <returns>Boolean</returns>
/// SKS IHB 9615
public boolean modified()
{
    boolean ret;
    SKS_PP_Bank         sks_pp_bank;

    ret = super();

    update_recordset sks_pp_bank
        setting IHB_Remit = sks_eft_setup.IHB_Remit
        where sks_pp_bank.SKS_IHBInhouseBank == NoYes::Yes;

    return ret;
}

/// <summary>
/// If flag is on remit attachment detail group should be enabled, else same will be disabled
/// </summary>
/// <returns>returns value </returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    RemitAttachDetail.enabled(SKS_EFT_Setup.APTemplateAttachment);
    BankAccountAttachDetail.enabled(SKS_EFT_Setup.APTemplateAttachment);
    return ret;
}

/// <summary>
/// If flag is on SRS1 remit attachment detail group should be enabled, else same will be disabled
/// </summary>
/// <returns>SKS_AP_Remit attachment detail</returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    APSRS1RemitAttachmentDetail.enabled(SKS_EFT_Setup.APSRS1Attachment);
    APSRSRemitAttachmentDetailV2.enabled(SKS_EFT_Setup.APSRS1Attachment || SKS_EFT_Setup.APSRS2Attachment);
    return ret;
}

/// <summary>
/// If flag is Yes SRS2 remit attachment detail group should be enabled, else same will be disabled
/// </summary>
/// <returns>SKS_DDR_Remit attachment detail</returns>
public boolean modified()
{
    boolean ret;

    ret = super();
    APSRS2RemitAttachmentDetail.enabled(SKS_EFT_Setup.APSRS2Attachment);
    APSRSRemitAttachmentDetailV2.enabled(SKS_EFT_Setup.APSRS1Attachment || SKS_EFT_Setup.APSRS2Attachment);
    return ret;
}

/// <summary>
/// Gets all SSRS reports for SRS1 remit attachment detail with 'SKS_AP' prefix.
/// </summary>
// SKS-FDD-BS-AP remit attach 9496
public void lookup()
{
    #ResAppl
    Object  formRun;
    container names;
    DictClass dictClass;
    List listSpecifications;
    ListEnumerator listEnumerator;
    ClassId sks_ddr_adviceRootClassId = classNum(SKS_APR_AttachAdviceController);

    formRun = classFactory.createSysLookupPicklist();
    formRun.init();

    listSpecifications = new DictClass(sks_ddr_adviceRootClassId).extendedBy();

    listEnumerator = listSpecifications.getEnumerator();
    while (listEnumerator.moveNext())
    {
        dictClass = new DictClass(listEnumerator.current());

        if (dictClass && !dictClass.isAbstract())
        {
            names += dictClass.name();
        }
    }

    formRun.choices(names, #ImageTable);
    formRun.caption("@SYS60851");
    this.performFormLookup(formRun);
}

/// <summary>
///Handles modification of the SKS_EFT_Setup_APSRS1EmailSSRS field
/// </summary>
/// <returns>boolean</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        element.EnableDisableHideDueDateOption();
    }
    return ret;
}

/// <summary>
///Handles modification of the SKS_EFT_Setup_APHideDueDateAtt2
/// </summary>
/// <returns>boolean</returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if(ret)
    {
        element.EnableDisableHideDueDateOption();
    }

    return ret;
}

