Public boolean isPPNumeric(str strValue)
{
    Boolean   xppBool;
    System.Boolean netBool;
    Str matchPattern = '^[0-9]*$';
    System.Text.RegularExpressions.Match myMatch;
    new InteropPermission(InteropKind::ClrInterop).assert();
    myMatch = System.Text.RegularExpressions.Regex::Match(strValue,matchPattern);
    netBool = myMatch.get_Success();
    xppBool = netBool;
    CodeAccessPermission::revertAssert();
    Return xppBool;
}

void changePreNoteStatus()
{
    SKS_EFT_FileFormat  sks_eft_fileformat;
    SKS_EFT_FileFormatPaymSpec  sks_eft_fileformatpaymspec;
    VendBankAccount vendbankaccount;
    VendPaymModeTable   vendpaymmodetable;
    ;


    select firstonly SKS_EFT_FormatID from vendpaymmodetable where vendpaymmodetable.PaymMode == SKS_EFT_VendorPaym.PaymMode
    join prenote, formatid from sks_eft_fileformat where sks_eft_fileformat.FormatId == vendpaymmodetable.SKS_EFT_FormatId
        && sks_eft_fileformat.PreNote == NoYes::Yes
    join prenote, formatid, paymspec from sks_eft_fileformatpaymspec
    where  sks_eft_fileformatpaymspec.FormatId == sks_eft_fileformat.FormatId
        && sks_eft_fileformatpaymspec.PaymSpec == sks_eft_vendorpaym.PaymSpec
        && sks_eft_fileformatpaymspec.PreNote == NoYes::Yes;

    if(vendpaymmodetable && SKS_EFT_Setup::IsPreNoteRequiredAP())
    {

        changeCompany(SKS_EFT_VendorPaym.VendCompany)
        {
            select firstonly forupdate vendbankaccount where vendbankaccount.AccountID == sks_eft_vendorpaym.VendBankAccountId
            && vendbankaccount.VendAccount == sks_eft_vendorpaym.VendAccount;

            if(vendbankaccount.SKS_EFT_PreNoteStatus == SKS_EB_PreNoteStatus::NotApplicable)
            {
                ttsbegin;

                vendBankAccount.SKS_EFT_PreNoteStatus = SKS_EB_PreNoteStatus::New;
                vendBankAccount.SKS_EFT_PreNoteDate = str2date('', 0);
                vendBankAccount.update();

                ttscommit;

                element.setBankAccountDetails();
            }
        }
    }
}

public void defaultLogisticsRecID()
{
    DirPartyContactInfoView dirpartycontactinfoview;
    VendTable   vendtable;
    ;

    vendtable = vendtable::find(SKS_EFT_VendorPaym.vendaccount);
    select firstOnly location from dirpartycontactinfoview where dirpartycontactinfoview.Party == Vendtable.Party
        && dirpartycontactinfoview.Type == LogisticsElectronicAddressMethodType::Email
        && dirpartycontactinfoview.IsPrimary == NoYes::Yes;

    sks_eft_vendorpaym.LogisticsLocationRecID = dirpartycontactinfoview.Location;
}

public boolean FindMastAcctList(SKS_EFT_FormatId  _FormatID)
{
    SKS_EFT_FileFormat  sks_eft_fileformat;

    select firstonly MastAcctList from sks_eft_fileformat
                where sks_eft_fileformat.FormatId == _FormatID;

    return sks_eft_fileformat.MastAcctList;

}

public void init()
{
    super();

    ccdPlusStr = 'CCD+';
    iatPlusStr = 'IAT';

    if (!element.args().caller())
    {
        throw error("@SYS96590");
    }
    mSKS_EB_Vendor = element.args().record();
    mMalStatus = SKS_EFT_MALStatus::New;
    select firstOnly mSks_eft_Setup;

    IsVisibleValue.text("@SKS1576");
}

void setBankAccountDetails()
{
    VendBankAccount vendBankAccount;
    ;

    changeCompany(mSKS_EB_Vendor.VendCompany)
    {
        select firstonly vendBankAccount where vendBankAccount.VendAccount == SKS_EFT_VendorPaym.VendAccount
            && vendBankAccount.AccountID == SKS_EFT_VendorPaym.VendBankAccountId;

        BankCodeType.selection(enum2int(vendBankAccount.BankCodeType));
        ACHBankTransit.text(vendBankAccount.RegistrationNum);
        ACHAccountNumber.text(vendBankAccount.AccountNum);
        SWIFTNo.text(vendBankAccount.SWIFTNo);
        BankIBAN.text(vendBankAccount.BankIBAN);
        ACHAccountType.selection(enum2int(vendBankAccount.BankAccountType));
        ACHPreNoteStatus.selection(vendBankAccount.SKS_EFT_PreNoteStatus);
        ACHApprovalDate.dateValue(vendBankAccount.SKS_EFT_PreNoteDate);
        AAStatus.selection(vendBankAccount.SKS_EFT_AcctStatus);
        AAChangedBy.text(vendBankAccount.SKS_EFT_AcctModifier);
        AAApprovedBy.text(vendBankAccount.SKS_EFT_AcctApprover);
    }
}

