void setProperties(str _maskValue, FormStringControl _formControl)
{
    ;

    if(_maskValue)
    {
        _formControl.visible(true);

        if (substr(_maskValue, 0, 1) == 'U')
        {
            _formControl.changeCase(2);
        }

        _formControl.limitText(strLen(_maskValue));
    }
    else
    {
        _formControl.visible(false);
        _formControl.changeCase(0);
        _formControl.limitText(0, AutoMode::Auto);
    }
}

void setVisbleEnabled()
{
    VendPaymModeTable   vendpaymmodetable;
    SKS_EFT_FileFormatPaymSpec  sks_eft_fileformatpaymspec;
    SKS_EFT_FileFormat  sks_eft_fileformat;
    boolean wireTabs;
    boolean wireTabBeneAcctNum;
    boolean preNote;
    ;

    IsVisibleValue.visible(false);
    preNote = false;
    select firstonly SKS_EFT_FormatID from vendpaymmodetable where vendpaymmodetable.PaymMode == SKS_EFT_VendorPaym.PaymMode;

    if(vendpaymmodetable)
    {
        select firstonly sks_eft_fileformatpaymspec where sks_eft_fileformatpaymspec.FormatId == vendpaymmodetable.SKS_EFT_FormatId
            && sks_eft_fileformatpaymspec.PaymSpec == SKS_EFT_VendorPaym.PaymSpec;

        if(sks_eft_fileformatpaymspec)
        {
            wireTabs = sks_eft_fileformatpaymspec.WireTabs;
            wireTabBeneAcctNum = sks_eft_fileformatpaymspec.BEBeneBankAcctReqd;
            preNote = sks_eft_fileformatpaymspec.PreNote;

            //Record Summary
            switch(sks_eft_fileformatpaymspec.RecordSummary)
            {
                case SKS_EFT_PaymSpecRecordSummary::AllAllowed:
                    SKS_EFT_VendorPaym_RecordSummary.enabled(true);
                    break;
                case SKS_EFT_PaymSpecRecordSummary::Invoice:
                    SKS_EFT_VendorPaym.RecordSummary = SKS_EFT_VendorRecordSummary::Invoice;
                    SKS_EFT_VendorPaym_RecordSummary.enabled(false);
                    break;
                case SKS_EFT_PaymSpecRecordSummary::Payment:
                    SKS_EFT_VendorPaym.RecordSummary = SKS_EFT_VendorRecordSummary::Payment;
                    SKS_EFT_VendorPaym_RecordSummary.enabled(false);
                    break;
                default:
                    break;
            }

            //CCD+
            if(sks_eft_fileformatpaymspec.PaymSpec == ccdPlusStr)
            {
                SKS_EFT_VendorPaym_EDIVersion.enabled(true);

            }
            else
            {
                SKS_EFT_VendorPaym_EDIVersion.enabled(false);

            }

            // Enable trading partner field
            SKS_EFT_VendorPaym_EDI820Layout.enabled(sks_eft_fileformatpaymspec.EDI);

            //MAL
            select firstonly MastAcctList from sks_eft_fileformat
                    where sks_eft_fileformat.FormatId == sks_eft_fileformatpaymspec.FormatId;

            if(sks_eft_fileformat.MastAcctList == NoYes::No)
            {
                MasterAccountList.enabled(false);
            }
            else
            {
                MasterAccountList.enabled(true);
            }

            //PreNote
            if(mSks_eft_Setup.APReqPreNote && preNote)
            {
                //Check the format ...PreNote flag
                select firstonly PreNote, FormatId from sks_eft_fileformat
                    where sks_eft_fileformat.PreNote == NoYes::Yes
                    && sks_eft_fileformat.FormatId == sks_eft_fileformatpaymspec.FormatId;

                if(!sks_eft_fileformat.PreNote)
                {
                    EFTPlusPreNoteInfo.visible(false);
                }
                else
                {
                    EFTPlusPreNoteInfo.visible(true);
                }
            }
            else
            {
                EFTPlusPreNoteInfo.visible(false);
            }

            //Account Approval Info
            if(mSks_eft_Setup.APAcctApproval)
            {
                AccountApprovalGroup.visible(true);
            }
            else
            {
                AccountApprovalGroup.visible(false);
            }

            if(EFTPlusPreNoteInfo.isVisible() == false && AccountApprovalGroup.isVisible() == false)
            {
                IsVisibleValue.visible(true);
            }
            else
            {
                IsVisibleValue.visible(false);
            }


            //Wire Tabs Used
            if(wireTabs)
            {
                WireTransfer1.enabled(true);
                WireTransfer2.enabled(true);

                if(substr(sks_eft_vendorpaym.PaymSpec, 0, 3) == iatPlusStr)
                {
                    WireTransfer3.enabled(false);
                }
                else
                {
                    WireTransfer3.enabled(true);
                }
            }
            else
            {
                WireTransfer1.enabled(false);
                WireTransfer2.enabled(false);
                WireTransfer3.enabled(false);
            }

            //WT - Bene Bank - Acct Number
            WireTransfer1.enabled(wireTabBeneAcctNum || wireTabs);
        }
    }
}

