public void setWBeneEmailFields()
{
    container locPrimaryEmail;
    DirPartyContactInfoview mDirPartyContactInfoview;
    SKS_EFT_EmailPurpose locSKS_EFT_EmailPurpose;

    locPrimaryEmail = SKS_EFT_VendorLoad::primaryEmail(SKS_EFT_VendorPaym.VendCompany, SKS_EFT_VendorPaym.VendAccount, SKS_EFT_VendorPaym.WBeneRemittanceLocation);
    mDirPartyContactInfoview = conPeek(locPrimaryEmail,2);

    if(mDirPartyContactInfoview.Locator)
    {
        locSKS_EFT_EmailPurpose = conPeek(locPrimaryEmail,1);
        WBeneEmailAddressType.selection(locSKS_EFT_EmailPurpose.SKS_EFT_EmailAddressType);
        WBeneVendorContactPurpose.text(locSKS_EFT_EmailPurpose.SKS_EB_ContactPurpose);
        WBeneEmailName.text(mDirPartyContactInfoview.LocationName);
        WBeneEmailAddress.text(mDirPartyContactInfoview.Locator);
    }
    else
    {
        WBeneEmailAddressType.clear();
        WBeneVendorContactPurpose.text('');
        WBeneEmailName.text('');
        WBeneEmailAddress.text('');
    }
}

Public void setWBeneFieldsEnabled()
{
    if (SKS_EFT_VendorPaym.WBeneUseVendAddr)
    {
        // 16714
        //SKS_EFT_Vendorload::UpdateVendorAddresses(SKS_EFT_VendorPaym, false, true, SKS_EFT_VendorPaym_WBeneRemittanceLocation.value()); // 10122
        if(element.FindMastAcctList(SKS_EFT_VendorPaym.FormatId))
        {
            SKS_EFT_VendorPaym_WBeneUseVendAddr.enabled(false);
        }
        else
        {
            SKS_EFT_VendorPaym_WBeneUseVendAddr.enabled(true);
        }
        SKS_EFT_VendorPaym_WBeneRemittanceLocation.enabled(true);
        SKS_EFT_VendorPaym_WBeneName.enabled(false);
        SKS_EFT_VendorPaym_WBeneAddr.enabled(false);
        SKS_EFT_VendorPaym_WBeneAddr2.enabled(false);
        SKS_EFT_VendorPaym_WBeneCity.enabled(false);
        SKS_EFT_VendorPaym_WBeneState.enabled(false);
        SKS_EFT_VendorPaym_WBeneZipPostal.enabled(false);
        SKS_EFT_VendorPaym_WBeneCountryRegionID.enabled(false);
    }
    else
    {
        SKS_EFT_VendorPaym_WBeneRemittanceLocation.enabled(false);
        SKS_EFT_VendorPaym.WBeneRemittanceLocation = 0; // 10122
        SKS_EFT_VendorPaym_WBeneName.enabled(true);
        SKS_EFT_VendorPaym_WBeneAddr.enabled(true);
        SKS_EFT_VendorPaym_WBeneAddr2.enabled(true);
        SKS_EFT_VendorPaym_WBeneCity.enabled(true);
        SKS_EFT_VendorPaym_WBeneState.enabled(true);
        SKS_EFT_VendorPaym_WBeneZipPostal.enabled(true);
        SKS_EFT_VendorPaym_WBeneCountryRegionID.enabled(true);
    }
}

public void updateMalStatus()
{
    SKS_EFT_FileFormat locSKS_EFT_FileFormat;
    SKS_EFT_MALStatus locSKS_EFT_MALStatus;

    if(SKS_EFT_VendorPaym.MALStatus != SKS_EFT_MALStatus::New
        && SKS_EFT_VendorPaym.MALStatus != SKS_EFT_MALStatus::Update
        && SKS_EFT_VendorPaym.MALStatus != SKS_EFT_MALStatus::NotApplic)
    {
        Select firstonly MastAcctList from locSKS_EFT_FileFormat where locSKS_EFT_FileFormat.formatID == SKS_EFT_VendorPaym.FormatId;
        if(locSKS_EFT_FileFormat.MastAcctList)
        {
            if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::Pending)
            {
                locSKS_EFT_MALStatus = SKS_EFT_MALStatus::New;
            }
            else if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::Approved)
            {
                locSKS_EFT_MALStatus = SKS_EFT_MALStatus::Update;
            }

            warning(strFmt("@SKS2634", locSKS_EFT_MALStatus));
            SKS_EFT_VendorPaym.MALStatus = locSKS_EFT_MALStatus;
        }
    }
}

public boolean validateFields()
{
    boolean ret = true;
    SKS_EFT_FileFormatPaymSpec lSKS_EFT_FileFormatPaymSpec;
    SKS_EFT_FileFormat lSKS_EFT_FileFormat;

    select firstonly BEBeneBankAcctReqd, BE01Reqd, BE02Reqd from lSKS_EFT_FileFormatPaymSpec where
                                                       lSKS_EFT_FileFormatPaymSpec.FormatId == SKS_EFT_VendorPaym.FormatId &&
                                                       lSKS_EFT_FileFormatPaymSpec.PaymSpec == SKS_EFT_VendorPaym.PaymSpec;

    if(substr(SKS_EFT_VendorPaym.PaymSpec, 0, 3) == iatPlusStr)
    {
        if(SKS_EFT_VendorPaym.WBeneName == '')
        {
            warning(strFmt("@SKS2643","@SYS70328" + ' >> ' + "@SYS117778")); //required field

            ret = false;
        }

        if(SKS_EFT_VendorPaym.WBeneAddr == '')
        {
            warning(strFmt("@SKS2643","@SYS70328" + ' >> ' + "@SYS119524")); //required field

            ret = false;
        }

        if(SKS_EFT_VendorPaym.WBeneCity == '')
        {
            warning(strFmt("@SKS2643","@SYS70328" + ' >> ' + "@SYS22249")); //required field

            ret = false;
        }

        if(SKS_EFT_VendorPaym.WBeneCountryRegionId == '')
        {
            warning(strFmt("@SKS2643","@SYS70328" + ' >> ' + "@SYS7437")); //required field

            ret = false;
        }

        if(SKS_EFT_VendorPaym.WBenBankName == '')
        {
            warning(strFmt("@SKS2643","@SKS1394" + ' >> ' + "@SYS117778")); //required field

            ret = false;
        }

        if(SKS_EFT_VendorPaym.WBenBankCountryRegionId == '')
        {
            warning(strFmt("@SKS2643","@SKS1394" + ' >> ' + "@SYS7437")); //required field

            ret = false;
        }
    }

    //WT - Bene Bank - Acct Number
    if(lSKS_EFT_FileFormatPaymSpec.BEBeneBankAcctReqd && SKS_EFT_VendorPaym.WBenBankAcct == '')
    {
        warning(strFmt("@SKS2643","@SKS1394" + ' >> ' + "@SYS24500")); //required field

        ret = false;
    }

    /*
    //BE01 Required
    if(lSKS_EFT_FileFormatPaymSpec.BE01Reqd && SKS_EFT_VendorPaym.BE01 == '')
    {
        warning(strFmt("@SKS2643","@SKS1389" + ' >> ' + SKS_EFT_VendorPaym_BE01.label())); //required field

        ret = false;
    }

    //BE02 Required
    if(lSKS_EFT_FileFormatPaymSpec.BE02Reqd && SKS_EFT_VendorPaym.BE02 == '')
    {
        warning(strFmt("@SKS2643","@SKS1389" + ' >> ' + SKS_EFT_VendorPaym_BE02.label())); //required field

        ret = false;
    }*/

    //Beneficiary Remittance Location
    if (SKS_EFT_VendorPaym.WBeneUseVendAddr && !SKS_EFT_VendorPaym.WBeneRemittanceLocation)
    {
        warning(strFmt("@SKS2643","@SKS1389" + ' >> ' + "@SYS302774")); //required field

        ret = false;
    }

    //MAL
    if(SKS_EFT_VendorPaym.LogisticsElectronicAddressRecId == 0)
    {
        select firstonly MastAcctList from lSKS_EFT_FileFormat
                        where lSKS_EFT_FileFormat.FormatId == SKS_EFT_VendorPaym.FormatId;

        if(lSKS_EFT_FileFormat.MastAcctList == NoYes::yes)
        {
            warning(strFmt("@SKS2643","@SKS2631" + ' >> ' + "@SKS2640")); //required field

            ret = false;
        }
    }

    return ret;
}

