        SKS_EFT_VendorPaym_WBenBankName.enabled(true);
        SKS_EFT_VendorPaym_WBenBankAddr.enabled(true);
        SKS_EFT_VendorPaym_WBenBankCity.enabled(true);
        SKS_EFT_VendorPaym_WBenBankCountryRegionId.enabled(true);
    }
}

