public void write()
{
    SKS_EFT_VendorPaym origVendPaym = SKS_EFT_VendorPaym.orig();
    VendBankAccount     vendBankAccount;

    ttsBegin;
    super();

    // if approvals are enabled and any key fields have changed, change the approval status on the
    // Vendor bank account back to pending.

    changecompany(mSKS_EB_Vendor.VendCompany) // 10324 -->
    {
        SKS_EFT_Setup    mSks_eft_setupLoc = SKS_EFT_Setup::find();
        if(mSks_eft_setupLoc.APAcctApproval)
        {
            if (origVendPaym.WBenBankAcct != SKS_EFT_VendorPaym.WBenBankAcct ||
                origVendPaym.WIntBankId != SKS_EFT_VendorPaym.WIntBankId ||
                origVendPaym.WIntBankAcct != SKS_EFT_VendorPaym.WIntBankAcct ||
                origVendPaym.WIntBankSwift != SKS_EFT_VendorPaym.WIntBankSwift ||
                origVendPaym.WCrePartyBankId != SKS_EFT_VendorPaym.WCrePartyBankId ||
                origVendPaym.WCrePartyAcct != SKS_EFT_VendorPaym.WCrePartyAcct ||
                origVendPaym.WCrePartySwift != SKS_EFT_VendorPaym.WCrePartySwift ||
                origVendPaym.WCrePartyName != SKS_EFT_VendorPaym.WCrePartyName)
            {
                select firstonly forUpdate SKS_EFT_AcctStatus, SKS_EFT_AcctModifier, SKS_EFT_AcctModifiedDate, SKS_EFT_AcctApprover, SKS_EFT_AcctApprovalDate, RecId
                    from vendBankAccount
                    where vendBankAccount.VendAccount == SKS_EFT_VendorPaym.VendAccount
                        && vendBankAccount.AccountID == SKS_EFT_VendorPaym.VendBankAccountId;

                if(vendBankAccount.RecId)
                {
                    vendBankAccount.SKS_EFT_AcctStatus = SKS_EFT_AcctApprovalStatus::Pending;
                    vendBankAccount.SKS_EFT_AcctModifier = curuserid();
                    vendBankAccount.SKS_EFT_AcctModifiedDate = DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone());
                    vendBankAccount.SKS_EFT_AcctApprover = '';
                    vendBankAccount.SKS_EFT_AcctApprovalDate = dateNull();
                    vendBankAccount.update();
                } // 10324 <--

                AAStatus.selection(vendBankAccount.SKS_EFT_AcctStatus);
                AAChangedBy.text(vendBankAccount.SKS_EFT_AcctModifier);
                AAApprovedBy.text(vendBankAccount.SKS_EFT_AcctApprover);
            }
        }
    }
    ttsCommit;
}

public boolean validateWrite()
{
    boolean ret;

    if(element.validateFields())
    {
        ret = super();
    }

    //SKS_FDD_16447/16448--->
    if(ret)
    {
        SKS_EFT_FileFormatPaymSpecFields    TmpPaymSpec;
        SKS_EFT_VendorPaymLookup    TmpVendorPaymLookup;

        while select required from tmpPaymSpec join TmpVendorPaymLookup
            where TmpVendorPaymLookup.fileformatPaymspecfieldsrecid == tmpPaymSpec.recid && 
            TmpVendorPaymLookup.VendorPaymRecId == SKS_EFT_VendorPaym.RecId
            && tmpPaymSpec.Required == NoYes::Yes
        {
            if(tmpVendorPaymLookup.fixedvalue == '' && tmpVendorPaymLookup.CodeLookupValuesRecId == 0)
            {
                error("@SKS2682");
                return false;
            }
        }
    }
    //SKS_FDD_16447/16448<---

    return ret;
}

public void delete()
{
    SKS_EFT_VendorPaym    tmpsks_eft_VendorPaym;
    ;

    super();
    select count(RecId) from tmpsks_eft_VendorPaym
           where tmpsks_eft_VendorPaym.VendCompany == mSKS_EB_Vendor.VendCompany
           && tmpsks_eft_VendorPaym.VendAccount == mSKS_EB_Vendor.VendAccount;
    if(!tmpsks_eft_VendorPaym.RecId)
    {
        BankAccounts.enabled(false);
        VendorDefaults.enabled(false);
        SKS_EFT_FileFormatNotes.enabled(false);
    }
}

