public boolean validate()
{
    boolean ret;
    ;

    ret = super();

    if(ret)
    {
        if(mMalStatus == SKS_EFT_MALStatus::New || mMalStatus == SKS_EFT_MALStatus::Update)
        {
            if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::Approved)
            {
                warning(strFmt("@SKS2634", SKS_EFT_VendorPaym.MALStatus));
                mMalStatus = SKS_EFT_MALStatus::Approved;
                ret = true;
            }
            else
            {
                error("@SKS2635");
                ret = false;
            }
        }
        else if(mMalStatus == SKS_EFT_MALStatus::Pending)
        {
            if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::New)
            {
                warning(strFmt("@SKS2634", SKS_EFT_VendorPaym.MALStatus));
                mMalStatus = SKS_EFT_MALStatus::New;
                ret = true;
            }
            else if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::Update)
            {
                warning(strFmt("@SKS2634", SKS_EFT_VendorPaym.MALStatus));
                mMalStatus = SKS_EFT_MALStatus::Update;
                ret = true;
            }
            else if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::NotApplic)
            {
                error("@SKS2636");
                ret = false;
            }

        }
        else if(mMalStatus == SKS_EFT_MALStatus::Approved)
        {
            if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::New)
            {
                warning(strFmt("@SKS2634", SKS_EFT_VendorPaym.MALStatus));
                mMalStatus = SKS_EFT_MALStatus::New;
                ret = true;
            }
            else if(SKS_EFT_VendorPaym.MALStatus == SKS_EFT_MALStatus::Update)
            {
                warning(strFmt("@SKS2634", SKS_EFT_VendorPaym.MALStatus));
                mMalStatus = SKS_EFT_MALStatus::Update;
                ret = true;
            }
            else
            {
                error("@SKS2637");
                ret =  false;
            }
        }
    }
    return ret;
}

public void modified()
{
    VendPaymModeTable vendPaymModeTable;
    ;

    super();

    SKS_EFT_VendorPaym.PaymSpec = '';
    SKS_EFT_VendorPaym.VendBankAccountId = '';
    SKS_EFT_Vendorload::UpdateVendorAddresses(SKS_EFT_VendorPaym, true);
    SKS_EFT_Vendorload::UpdateVendorBankAddresses(SKS_EFT_VendorPaym, true);

    if(SKS_EFT_VendorPaym.PaymMode != '')
    {
        vendPaymModeTable = VendPaymModeTable::find(SKS_EFT_VendorPaym.PaymMode);
        SKS_EFT_VendorPaym.FormatId = vendPaymModeTable.SKS_EFT_FormatId;

        if(!element.FindMastAcctList(SKS_EFT_VendorPaym.FormatId))
        {
            SKS_EFT_VendorPaym.MALStatus = SKS_EFT_MALStatus::NotApplic;
            SKS_EFT_VendorPaym_MALStatus.enabled(false);
            SKS_EFT_VendorPaym_WBeneUseVendAddr.enabled(true);
        }
        else
        {
            SKS_EFT_VendorPaym.MALStatus = SKS_EFT_MALStatus::New;
            SKS_EFT_VendorPaym_MALStatus.enabled(true);
            SKS_EFT_VendorPaym.WBeneUseVendAddr = NoYes::Yes;
            SKS_EFT_VendorPaym_WBeneUseVendAddr.enabled(false);
            element.setWBeneFieldsEnabled();
        }
    }

    element.defaultLogisticsRecID();
    SKS_EFT_VendorPaym_ds.refresh();
}

public void modified()
{
    SKS_EFT_FileFormatPaymSpec SKS_EFT_FileFormatPaymSpec;
    super();
    element.setMaskAndLengthValues();
    element.setVisbleEnabled();

    select firstOnly EDI820DefaultPartner, FormatId, PaymSpec, WireTabs from SKS_EFT_FileFormatPaymSpec
        where SKS_EFT_FileFormatPaymSpec.FormatId == SKS_EFT_VendorPaym.FormatId &&
              SKS_EFT_FileFormatPaymSpec.PaymSpec == SKS_EFT_VendorPaym.PaymSpec;

    if( !element.FindMastAcctList(SKS_EFT_FileFormatPaymSpec.FormatId))
    {
        if(SKS_EFT_FileFormatPaymSpec.WireTabs)
        {
            SKS_EFT_Vendorload::UpdateVendorAddresses(SKS_EFT_VendorPaym);
        }
        else
        {
            SKS_EFT_Vendorload::UpdateVendorAddresses(SKS_EFT_VendorPaym, true);
        }
    }
    else
    {
        SKS_EFT_Vendorload::UpdateVendorAddresses(SKS_EFT_VendorPaym, false, true);
    }

    SKS_EFT_VendorPaym.EDI820Layout = SKS_EFT_FileFormatPaymSpec.EDI820DefaultPartner;

    //SKS_DevOps_17129--->
    if(SKS_EFT_VendorPaym.FormatId == 'US-ACH' && SKS_EFT_VendorPaym.PaymSpec == 'CCD+')
    {
        SKS_EFT_VendorPaym.EDIVersion = SKS_EFT_EDIVersion::v003040;
    }
    //SKS_DevOps_17129<---

    SKS_EFT_VendorPaym.VendBankAccountId = '';
    SKS_EFT_Vendorload::UpdateVendorBankAddresses(SKS_EFT_VendorPaym, true);
    SKS_EFT_VendorPaym_ds.refresh();
    element.setWBeneEmailFields();
    element.setWBeneFieldsEnabled();
}

public boolean validate()
{
    boolean ret;
    VendBankAccount     vendBankAccount;
    VendPaymModeTable   vendpaymmodetable;
    SKS_EFT_FileFormatPaymSpec  sks_eft_fileformatpaymspec;
    ;

    ret = super();

    if (ret)
    {

        select firstonly SKS_EFT_FormatID from vendpaymmodetable where vendpaymmodetable.PaymMode == SKS_EFT_VendorPaym.PaymMode;

        if(vendpaymmodetable)
        {
            select firstonly sks_eft_fileformatpaymspec where sks_eft_fileformatpaymspec.FormatId == vendpaymmodetable.SKS_EFT_FormatId
                && sks_eft_fileformatpaymspec.PaymSpec == SKS_EFT_VendorPaym.PaymSpec;

            if(sks_eft_fileformatpaymspec)
            {
                if (SKS_EFT_Vendorpaym.VendCompany == '')
                {
                    SKS_EFT_VendorPaym.VendCompany = curext();
                }

                changeCompany(SKS_EFT_VendorPaym.VendCompany)
                {
                    select firstonly vendBankAccount where vendBankAccount.VendAccount == SKS_EFT_VendorPaym.VendAccount
                        && vendBankAccount.AccountID == SKS_EFT_VendorPaym.VendBankAccountId;

                }

                // Routing number
                if(sks_eft_fileformatpaymspec.VBARoutingReqd && !vendBankAccount.RegistrationNum)
                {
                    ret = checkfailed("@SKS1485");
                }

                if(sks_eft_fileformatpaymspec.VBAAccountReqd && !vendBankAccount.AccountNum)
                {
                    ret = checkfailed("@SKS1486");
                }

                if(sks_eft_fileformatpaymspec.VBASwiftReqd && !vendBankAccount.SWIFTNo)
                {
                    ret = checkfailed("@SKS1711");
                }

                if(sks_eft_fileformatpaymspec.VBAIBANReqd && !vendBankAccount.BankIBAN)
                {
                    ret = checkfailed("@SKS1712");
                }

            }

        }
    }

    return ret;
}

public void modified()
{
    SKS_EFT_FileFormatPaymSpec SKS_EFT_FileFormatPaymSpec;
    ;

    super();
    select firstOnly EDI820DefaultPartner, FormatId, PaymSpec, WireTabs from SKS_EFT_FileFormatPaymSpec
        where SKS_EFT_FileFormatPaymSpec.FormatId == SKS_EFT_VendorPaym.FormatId &&
              SKS_EFT_FileFormatPaymSpec.PaymSpec == SKS_EFT_VendorPaym.PaymSpec;

    if(SKS_EFT_FileFormatPaymSpec.WireTabs)
    {
        SKS_EFT_Vendorload::UpdateVendorBankAddresses(SKS_EFT_VendorPaym);
        SKS_EFT_VendorLoad::UpdateVendorIntermediaryBank(SKS_EFT_VendorPaym);
        SKS_EFT_Vendorload::UpdateVendorCreditParty(SKS_EFT_VendorPaym);
    }
    else
    {
        SKS_EFT_Vendorload::UpdateVendorBankAddresses(SKS_EFT_VendorPaym, true);
        SKS_EFT_VendorLoad::UpdateVendorIntermediaryBank(SKS_EFT_VendorPaym, true);
        SKS_EFT_Vendorload::UpdateVendorCreditParty(SKS_EFT_VendorPaym, true);
    }

    element.setBankAccountDetails();
    element.changePreNoteStatus();
    //SKS 9536
    element.setWBeneBankFieldsvalue();
    element.setWIntBankFieldsvalue();
    element.setWCrePartyFieldsvalue();
    //SKS 9536
    //SKS_EFT_VendorPaym_ds.refresh();

    SKS_EFT_FileFormatPaymSpecFields_ds.research(true);
    SKS_EFT_VendorPaym_ds.research(true);
}

