void clicked()
{
    VendTable       vendTable;
    DialogButton    diagButton;
    ;

    diagButton = box::yesNo(strfmt("@SKS1492", SKS_EFT_VendorPaym.VendAccount), DialogButton::Ok, "@SKS311");

    if(diagButton == DialogButton::Yes)
    {

        ttsbegin;
        changecompany(SKS_EFT_VendorPaym.VendCompany)
        {
            if(VendTable::exist(SKS_EFT_VendorPaym.VendAccount))
            {
                select firstonly forupdate * from vendTable where vendTable.AccountNum == SKS_EFT_VendorPaym.VendAccount;

                vendTable.PaymMode = SKS_EFT_VendorPaym.PaymMode;
                vendTable.PaymSpec = SKS_EFT_VendorPaym.PaymSpec;
                vendTable.BankAccount = SKS_EFT_VendorPaym.VendBankAccountId;
                vendTable.update();
            }

        }
        ttscommit;

    }


    SKS_EFT_VendorPaym_ds.research(true);

    super();
}

