public void lookup()
{
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildDataSource    bankAccountDataSource;

    SysTableLookup  sysTableLookup = sysTableLookup::newParameters(tableNum(BankAccountTable), this);
    ;

    sysTableLookup.addLookupfield(fieldNum(BankAccountTable, AccountId));
    sysTableLookup.addLookupfield(fieldnum(BankAccountTable, Name));

    queryBuildDataSource = query.addDataSource(tableNum(BankAccountTable));
    bankAccountDataSource = queryBuildDataSource.addDataSource(tablenum(SKS_EFT_Bank));

    bankAccountDataSource.relations(true);
    bankAccountDataSource.joinMode(JoinMode::ExistsJoin);

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public void lookup()
{
    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    SysTableLookup          sysTableLookup;
    ;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    sysTableLookup = SysTableLookup::newParameters(tableNum(SKS_EFT_EDI820), this);

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_EDI820, CompanyName));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_EDI820, Description));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(SKS_EFT_EDI820));

    queryBuildRange = queryBuildDataSource.addRange(
        fieldNum(SKS_EFT_EDI820, FormatId));
    queryBuildRange.value(SKS_EFT_VendorPaym.FormatId);
    queryBuildRange.status(RangeStatus::Hidden);

    queryBuildRange = queryBuildDataSource.addRange(
        fieldNum(SKS_EFT_EDI820, PaymSpec));
    queryBuildRange.value(strFmt('%1,""',SKS_EFT_VendorPaym.PaymSpec));
    queryBuildRange.status(RangeStatus::Hidden);

    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

