public Common lookupReference()
{
    LogisticsElectronicAddress   RecID;
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource, qbds2;
    VendTable               vendtable;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(LogisticsElectronicAddress), ReferenceGroup, true);
    ;
    vendtable = vendtable::find(SKS_EFT_VendorPaym.vendaccount);

    query.allowCrossCompany(true);

    // Add fields to be shown in the lookup form
    // Limit and arrange the data selection

    queryBuildDataSource = query.addDataSource(tableNum(LogisticsElectronicAddress));
    queryBuildDataSource.addRange(fieldNum(LogisticsElectronicAddress, type)).value(queryValue(LogisticsElectronicAddressMethodType::Email));

    qbds2 = queryBuildDataSource.addDataSource(tableNum(DirPartyLocation));
    qbds2.addLink(fieldNum(LogisticsElectronicAddress, location), fieldNum(DirPartyLocation, location));
    qbds2.joinMode(JoinMode::InnerJoin);

    qbds2.addRange(fieldNum(DirPartyLocation, Party)).value(queryvalue(VendTable.party));

    sysTableLookup.addLookupField(fieldNum(LogisticsElectronicAddress, Locator));
    sysTableLookup.addLookupField(fieldNum(LogisticsElectronicAddress, Description));

    sysTableLookup.parmQuery(query);

    // Perform the lookup
    RecId = sysTableLookup.performFormLookup();

    return RecID;
}

