void BankFabricFieldsInit()
{
    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly sks_EB_FileStorage;
    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        SKS_EFTAR_Bank_ARFileName.visible(false);
        SKS_EFTAR_Bank_ARFilePath.visible(false);
        SKS_EFTAR_Bank_ARPathArchive.visible(false);
        SKS_EFTAR_Bank_SKS_DD_InboundFileLoc.visible(false);
        SKS_EFTAR_Bank_ARControlFileName.visible(false);
    }
    else
    {
        SKS_EFTAR_Bank_ARBankFabricFileLocation.visible(false);
        SKS_EFTAR_Bank_SKS_DD_BFInboundFileLoc.visible(false);
        SKS_EFTAR_Bank_ARBankFabricFileNames.visible(false);
    }
}

