void loadSKS_EFT_FileFormat()
{
    ;

    if(SKS_EFTAR_Bank.PaymMode != custpaymmodetable.PaymMode)
    {
        select SKS_EFT_FormatID from custpaymmodetable
            where custpaymmodetable.PaymMode == SKS_EFTAR_Bank.PaymMode
        join
            sks_eft_fileFormat where sks_eft_fileFormat.FormatId == custpaymmodetable.SKS_EFT_FormatId;
    }
}

void setMaskAndLengthValues()
{
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspec;
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspecCpny;
    Int   CpnyNM;
    ;

    if(custpaymmodetable && sks_eft_fileformat)
    {
        //Company Info
        CpnyNm = 0;
        Select firstonly FormatID from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == custpaymmodetable.SKS_EFT_FormatId && sks_eft_Fileformatpaymspec.TxnNacha == NoYes::Yes &&
            sks_eft_Fileformatpaymspec.Selected == NoYes::Yes;
        Select firstonly FormatID from sks_eft_fileformatpaymspecCpny
            where sks_eft_fileformatpaymspecCpny.formatid == custpaymmodetable.SKS_EFT_FormatId && sks_eft_fileformatpaymspecCpny.CpnyIDNameReq == NoYes::Yes &&
            sks_eft_fileformatpaymspecCpny.Selected == NoYes::Yes;

        //Display CompanyID
        If(sks_eft_fileformat.CompanyIdTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFTAR_Bank_ACHCompanyID.label(sks_eft_fileformat.CompanyIdTitle);
            element.setProperties(sks_eft_fileformat.CompanyIdMask, SKS_EFTAR_Bank_ACHCompanyID);
            SKS_EFTAR_Bank_ACHCompanyID.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFTAR_Bank_ACHCompanyID.visible(False);
        }
        //Display Company Name
        IF(sks_eft_fileformat.CompanyNameTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFTAR_Bank_ACHCompanyName.label(sks_eft_fileformat.CompanyNameTitle);
            element.setProperties(sks_eft_fileformat.CompanyNameMask, SKS_EFTAR_Bank_ACHCompanyName);
            SKS_EFTAR_Bank_ACHCompanyName.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFTAR_Bank_ACHCompanyName.visible(False);
        }

        If (!CpnyNm)
        {
            SKS_EFTAR_Bank_CompanyIdUnique.visible(False);
            PPD.visible(False);
            CCD.visible(False);
        }
        else
        {
            SKS_EFTAR_Bank_CompanyIdUnique.visible(True);
            PPD.visible(True);
            CCD.visible(True);
            If(SKS_EFTAR_Bank.CompanyIDUnique==True)
            {
                sks_eftar_bank_CompanyIDPPD.enabled(True);
                sks_eftar_bank_CompanyIDCCD.enabled(True);
                sks_eftar_bank_CompanyNamePPD.enabled(True);
                sks_eftar_bank_CompanyNameCCD.enabled(True);
            }
            else
            {
                sks_eftar_bank_CompanyIDPPD.enabled(False);
                sks_eftar_bank_CompanyIDCCD.enabled(False);
                sks_eftar_bank_CompanyNamePPD.enabled(False);
                sks_eftar_bank_CompanyNameCCD.enabled(False);
            }
        }

        if(sks_eft_fileformat.ImmDestNameTitle)
        {
            //Immediate destination
            SKS_EFTAR_Bank_ACHImmDest.label(sks_eft_fileformat.ImmDestTitle);
            element.setProperties(sks_eft_fileformat.ImmDestMask, SKS_EFTAR_Bank_ACHImmDest);

            SKS_EFTAR_Bank_ACHImmDestName.label(sks_eft_fileformat.ImmDestNameTitle);
            element.setProperties(sks_eft_fileformat.ImmDestNameMask, SKS_EFTAR_Bank_ACHImmDestName);
            SKS_EFTAR_Bank_ACHImmDestName.visible(True);
        }
        else
        {
            SKS_EFTAR_Bank_ACHImmDestName.visible(False);
        }

        IF(sks_eft_fileformat.ImmOriginNameTitle)
        {
            //Immediate originator
            SKS_EFTAR_Bank_ACHImmOrig.label(sks_eft_fileformat.ImmOriginTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginMask, SKS_EFTAR_Bank_ACHImmOrig);

            SKS_EFTAR_Bank_ACHImmOrigName.label(sks_eft_fileformat.ImmOriginNameTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginNameMask, SKS_EFTAR_Bank_ACHImmOrigName);
            SKS_EFTAR_Bank_ACHImmOrigName.visible(True);
        }
        else
        {
            SKS_EFTAR_Bank_ACHImmOrigName.visible(False);
        }

        /*
        //Wire Plus values
        SKS_EFTAR_Bank_W01.label(sks_eft_fileformat.WBank01Description);
        element.setProperties(sks_eft_fileformat.WBank01Mask, SKS_EFTAR_Bank_W01);

        SKS_EFTAR_Bank_W02.label(sks_eft_fileformat.WBank02Description);
        element.setProperties(sks_eft_fileformat.WBank02Mask, SKS_EFTAR_Bank_W02);

        SKS_EFTAR_Bank_W03.label(sks_eft_fileformat.WBank03Description);
        element.setProperties(sks_eft_fileformat.WBank03Mask, SKS_EFTAR_Bank_W03);

        SKS_EFTAR_Bank_W04.label(sks_eft_fileformat.WBank04Description);
        element.setProperties(sks_eft_fileformat.WBank04Mask, SKS_EFTAR_Bank_W04);*/
    }
}

void setProperties(str _maskValue, FormStringControl _formControl)
{
    ;

    if(_maskValue)
    {
        _formControl.visible(true);

        if (substr(_maskValue, 0, 1) == 'U')
        {
            _formControl.changeCase(2);
        }

        _formControl.limitText(strLen(_maskValue));
    }
    else
    {
        _formControl.visible(false);
        _formControl.changeCase(0);
        _formControl.limitText(0, AutoMode::Auto);
    }
}

