Public boolean isPPNumeric(str strValue)
{
    Boolean   xppBool;
    System.Boolean netBool;
    Str matchPattern = '^[0-9]*$';
    System.Text.RegularExpressions.Match myMatch;
    new InteropPermission(InteropKind::ClrInterop).assert();
    myMatch = System.Text.RegularExpressions.Regex::Match(strValue,matchPattern);
    netBool = myMatch.get_Success();
    xppBool = netBool;
    CodeAccessPermission::revertAssert();
    Return xppBool;
}

public int active()
{
    int ret;

    ret = super();

    element.loadSKS_EFT_FileFormat();

    element.disableCompanyInfo();
    element.disableFileHeader();
    element.disableEMail();
    element.disableFileInfo();
    element.disablePostedPaymInfo();
    element.sks_disableInternalContact(); //SKS_DDR_Remit attachment

    element.setMaskAndLengthValues();
    element.enabledisablecontrolfile();

    SKS_EFTAR_Bank_ds.object(fieldnum(SKS_EFTAR_Bank, ARPathARchive)).enabled(SKS_EFTAR_Bank.ARArchive);
    return ret;
}

public boolean validateWrite()
{
    boolean ret;
    CustPaymModeTable  lCustPaymModeTable;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFTAR_Bank_ARBankFabricFileLocation.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFTAR_Bank_ARBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFTAR_Bank_ARFileName.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFTAR_Bank_ARFileName.setFocus();

            return false;
        }

        if(!SKS_EFTAR_Bank_ARFilePath.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFTAR_Bank_ARFilePath.setFocus();

            return false;
        }

        if(SKS_EFTAR_Bank.ARArchive && !SKS_EFTAR_Bank.ARPathArchive && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            warning(strFmt("@SKS2643","@SKS75")); //required field

            return false;
        }
    }

    if(SKS_EFTAR_Bank.ARCommID == '')
    {
        warning(strFmt("@SKS2643","@SKS2430")); //required field

        return false;
    }

    if(SKS_EFTAR_Bank.AllowPostedPmtReversal == NoYes::Yes)
    {
        if(SKS_EFTAR_Bank.RevPaymMode == '')
        {
            warning(strFmt("@SKS2643","@SKS3012")); //required field

            return false;
        }

        if(SKS_EFTAR_Bank.AutoPostPaymentJournal == NoYes::Yes)
        {
            select firstonly Paymstatus from lCustPaymModeTable where lCustPaymModeTable.PaymMode == SKS_EFTAR_Bank.RevPaymMode;

            if(lCustPaymModeTable.PaymStatus != CustVendPaymStatus::None)
            {
                error("@SKS3014");

                return false;
            }
        }
    }

    if(SKS_EFTAR_Bank.ARControlFileFormatId != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFTAR_Bank.ARBankFabricFileNames == '')
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFFileNames")); //required field

                return false;
            }
        }
        else
        {
            if(SKS_EFTAR_Bank.ARControlFileName == '')
            {
                warning(strFmt("@SKS2643","@SKS3242")); //required field

                return false;
            }
        }
    }

    ret = super();
    //<SKS-begin>
    ret = SKS_EFTAR_Bank.SKS_DD_FileLocationValide(SKS_EFTAR_Bank);
    //</SKS-end>

    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFTAR_Bank.AREMTemplateNoAttach, SKS_EFTAR_Bank.AREMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFTAR_Bank.AREMTemplateAttach, SKS_EFTAR_Bank.AREMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFTAR_Bank.ARAENTemplateNoAttach, SKS_EFTAR_Bank.AREMTemplateLanguage) && ret;
    ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFTAR_Bank.ARAENTemplateAttach, SKS_EFTAR_Bank.AREMTemplateLanguage) && ret;

    //SKS_FDD_16447/16448--->
    if(ret)
    {
        SKS_EFT_FileFormatFields            lSKS_EFT_FileFormatFields;
        SKS_EFT_ARBankAccountPaymLookup     lSKS_EFT_BankAccountPaymLookup;

        while select required from lSKS_EFT_FileFormatFields join lSKS_EFT_BankAccountPaymLookup
            where lSKS_EFT_BankAccountPaymLookup.FileFormatFieldsRecId == lSKS_EFT_FileFormatFields.recid &&
            lSKS_EFT_BankAccountPaymLookup.BankPaymRecId == SKS_EFTAR_Bank.RecId
            && lSKS_EFT_FileFormatFields.Required == NoYes::Yes
        {
            if(lSKS_EFT_BankAccountPaymLookup.fixedvalue == '' && lSKS_EFT_BankAccountPaymLookup.CodeLookupValuesRecId == 0)
            {
                error("@SKS2682");
                return false;
            }
        }
    }
    //SKS_FDD_16447/16448<---

    return ret;
}

