/// <summary>
/// Lookup method for field.
/// </summary>
/// <param name = "_formControl">
/// Form control object.
/// </param>
/// <param name = "_filterStr">
/// Filter string.
/// </param>
public void lookup(FormControl _formControl, str _filterStr)
{

    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(LedgerJournalName), _formControl);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, JournalName));
    sysTableLookup.addLookupField(fieldNum(LedgerJournalName, Name));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(LedgerJournalName));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(LedgerJournalName, JournalType));
    queryBuildRange.value(enum2str(LedgerJournalType::CustPayment));
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
    //</SKS-end>
}

