/// <summary>
///  CommunicationID lookup
/// </summary>
public void lookup()
{
    //<SKS-begin>
    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    SKS_EB_CommProcess      sks_EB_CommProcessloc;

    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
    SysTableLookup::newParameters(tableNum(SKS_EB_CommProcess), SKS_EFTAR_Bank_SKS_DD_InCommId);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(SKS_EB_CommProcess, CommId));
    sysTableLookup.addLookupField(fieldNum(SKS_EB_CommProcess, Direction));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(SKS_EB_CommProcess));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EB_CommProcess, CommId));

    select CommID from sks_EB_CommProcessloc
        where sks_EB_CommProcessloc.CommID == SKS_EFTAR_Bank.SKS_DD_InCommId;

    queryBuildRange.value(sks_EB_CommProcessloc.CommID);
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
    //</SKS-end>
}

