public void lookup()
{
    Query                   query          = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    CustPaymModeTable       localCustPaymModeTable;
    // Create an instance of SysTableLookup where 'this'
    // is the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(SKS_EFT_ControlFiles), SKS_EFTAR_Bank_ARControlFileFormatiD);
    ;

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_ControlFiles, EFTControlFileFormatID));
    sysTableLookup.addLookupField(fieldNum(SKS_EFT_ControlFiles, EFTControlFileDescription));

    // Limit and arrange the data selection.
    queryBuildDataSource = query.addDataSource(tableNum(SKS_EFT_ControlFiles));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFT_ControlFiles, FileFormatID));
    select firstonly sks_eft_formatid from localCustPaymModeTable where localCustPaymModeTable.PaymMode == SKS_EFTAR_Bank.paymmode;
    queryBuildRange.value(localCustPaymModeTable.sks_eft_formatid);
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

