public void activate(boolean _active)
{
    SKS_EB_FileStorage sks_EB_FileStorage;

    SKS_EFT_Setup_ARArchive.archivecheckbox_chk();

    // FDD_11060 -->
    SKS_EFT_Setup_ARCreateSendCompany.enabled(SKS_EFT_Setup.ARCreateSendLater == NoYes::Yes && SKS_PP_Setup.modMCFAPDD );
    if(!SKS_EFT_Setup.ARCreateSendCompany)
    {
        SKS_EFT_Setup.ARCreateSendCompany = curExt();
    }
    // FDD_11060 <--

    this.isEmailEnabled();

    super(_active);
}

public boolean canClose()
{
    boolean ret;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFT_Setup_ARBankFabricFileLocation.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFT_Setup_ARFileName.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARFileName.setFocus();

            return false;
        }

        if(!SKS_EFT_Setup_ARFilePath.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARFilePath.setFocus();

            return false;
        }

        if(SKS_EFT_Setup_ARArchive.value() && !SKS_EFT_Setup_ARPathArchive.valueStr() && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARPathArchive.setFocus();

            return false;
        }
    }

    ret = super();

    return ret;
}

public void close()
{
    super();
    element.args().caller().refreshparentinfo();
}

private void enableEmailTab(boolean _visibleValue)
{
    Email.visible(_visibleValue);
    EmailActive.visible(_visibleValue);
    SKS_DD_DirectDebit.visible(_visibleValue);
    EmailGroup.visible(_visibleValue);
    AdvanceEmailGroup.visible(_visibleValue);
    ReplyTo.visible(_visibleValue);
    SKS_DDR_RemitAttachmentOptions.visible(_visibleValue);
    Company.visible(_visibleValue);
    CustomerEmailAddress.visible(_visibleValue);
    Group1.visible(_visibleValue);
    BankAccountAttachDetail.visible(_visibleValue);
    SKS_DDR_SRS1RemitAttachmentDetail.visible(_visibleValue);
    SKS_DDR_SRS2RemitAttachmentDetail.visible(_visibleValue);

    if (_visibleValue)
    {
        Group1.enabled(sks_EFT_Setup.SKS_DDR_TemplateAttachment);
        BankAccountAttachDetail.enabled(sks_EFT_Setup.SKS_DDR_TemplateAttachment);
        SKS_DDR_SRS1RemitAttachmentDetail.enabled(sks_EFT_Setup.SKS_DDR_SRS1Attachment);
        SKS_DDR_SRS2RemitAttachmentDetail.enabled(sks_EFT_Setup.SKS_DDR_SRS2Attachment);
    }
}

public void init()
{
    SKS_EFT_Setup      sks_EFT_Setup_Loc;
    boolean            updateRecord;
    ;

    super();

    if (!element.args().caller())
    {
        throw error("@SYS96590");
    }

    AttachmentMessage.text("@SKS1501");
    AttachmentPrompt.text("@SKS:SKGBankAttachName");

    //SKS-Begin
    // Controlling the visibility of direct debit fast tab based on TAS paramter setup.
    if (SKS_PP_setup::find().RegAREFT)
    {
        SKS_DD_DirectDebit.visible(true);
    }
    else
    {
        SKS_DD_DirectDebit.visible(false);
    }
    //SKS-End

    //SKS_DDR_Remit attachment detail -Begin
    sks_EFT_Setup_Loc = SKS_EFT_Setup::find(true);
    SKS_DDR_SRS1RemitAttachmentDetail.enabled(sks_EFT_Setup_Loc.SKS_DDR_SRS1Attachment);
    SKS_DDR_SRS2RemitAttachmentDetail.enabled(sks_EFT_Setup_Loc.SKS_DDR_SRS2Attachment);
    Group1.enabled(sks_EFT_Setup_Loc.SKS_DDR_TemplateAttachment);
    BankAccountAttachDetail.enabled(sks_EFT_Setup_Loc.SKS_DDR_TemplateAttachment);
    SKS_EFT_Setup_ARPeriodsRet.enabled(sks_EFT_Setup_Loc.ARPurgeFiles);
    //SKS_DDR_Remit attachment detail - End

    if(!sks_EFT_Setup_Loc.Reminder1AfterDays )
    {
        sks_EFT_Setup_Loc.Reminder1AfterDays = 30;
        updateRecord = true;
    }
    if(!sks_EFT_Setup_Loc.Reminder2AfterDays)
    {
        sks_EFT_Setup_Loc.Reminder2AfterDays = 30;
        updateRecord = true;
    }
    if(!sks_EFT_Setup_Loc.CancelDays)
    {
        sks_EFT_Setup_Loc.CancelDays = 30;
        updateRecord = true;
    }

    if(updateRecord)
    {
        updateRecord =  false;
        sks_EFT_Setup_Loc.update();
    }
}

