public void enableSendAdvEmail()
{
    if(SKS_EFT_Setup.ARCreateSendLater && SKS_EFT_Setup.ARAdvEmailNotify)
    {
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoAdvEmail)).enabled(true);
        this.enableAutoSendEffectiveDate();
    }
    else
    {
        SKS_EFT_Setup.AutoAdvEmail = NoYes::No;
        SKS_EFT_Setup.AutoSendFileOnEffectiveDate = NoYes::No;
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoAdvEmail)).enabled(false);
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoSendFileOnEffectiveDate)).enabled(false);
        //SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoSendFileBatchJobId)).enabled(false);
        SKS_EFT_Setup_AutoSendFileBatchJobId.enabled(false);
        CreateAutoSendFileBatchJob.enabled(SKS_EFT_Setup.AutoSendFileOnEffectiveDate);
        ViewAutoSendFileBatchJob.enabled(SKS_EFT_Setup.AutoSendFileOnEffectiveDate);
    }
}

public void enableAutoSendEffectiveDate()
{
    if(SKS_EFT_Setup.AutoAdvEmail && SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoAdvEmail)).enabled())
    {
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoSendFileOnEffectiveDate)).enabled(true);
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoSendFileBatchJobId)).enabled(SKS_EFT_Setup.AutoSendFileOnEffectiveDate ? true : false);
    }
    else
    {
        SKS_EFT_Setup.AutoSendFileOnEffectiveDate = NoYes::No;
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoSendFileOnEffectiveDate)).enabled(false);
        SKS_EFT_Setup_ds.object(fieldNum(SKS_EFT_Setup, AutoSendFileBatchJobId)).enabled(false);
        CreateAutoSendFileBatchJob.enabled(SKS_EFT_Setup.AutoSendFileOnEffectiveDate);
        ViewAutoSendFileBatchJob.enabled(SKS_EFT_Setup.AutoSendFileOnEffectiveDate);
    }
}

public boolean validateWrite()
{
    //<SKS-begin>
    CustPaymModeTable   lCustPaymModeTable;
    //</SKS-end>
    boolean ret;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFT_Setup_ARBankFabricFileLocation.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFT_Setup_ARFileName.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARFileName.setFocus();

            return false;
        }

        if(!SKS_EFT_Setup_ARFilePath.valueStr())
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARFilePath.setFocus();

            return false;
        }

        if(SKS_EFT_Setup_ARArchive.value() && !SKS_EFT_Setup_ARPathArchive.valueStr() && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            Box::warning("@SKS253","@SKS225"); //required field

            SKS_EFT_Setup_ARPathArchive.setFocus();

            return false;
        }
    }

    if(SKS_EFT_Setup.ARCommID == '')
    {
        warning("@SKS253","@SKS225"); //required field

        SKS_EFT_Setup_CommID.setFocus();

        return false;
    }

    //<SKS-begin>
    if(SKS_EFT_Setup.SKS_DD_AllowPostedPmtJournal == NoYes::Yes)
    {
        if(SKS_EFT_Setup.SKS_DD_RevPaymMode == '')
        {
            warning("@SKS253","@SKS3012"); //required field
            SKS_EFT_Setup_SKS_DD_RevPaymMode.setfocus();

            return false;
        }

        if(SKS_EFT_Setup.SKS_DD_AutoPostJournal == NoYes::Yes)
        {
            select firstonly Paymstatus from lCustPaymModeTable where lCustPaymModeTable.PaymMode == SKS_EFT_Setup.SKS_DD_RevPaymMode;

            if(lCustPaymModeTable.PaymStatus != CustVendPaymStatus::None)
            {
                error("@SKS3014");

                return false;
            }
        }
    }
    //</SKS-end>
    // SKS_EB_FDD-DD-NETS integration -->
    if(SKS_EFT_Setup.LegalId == SKS_EB_LegalId::Blank && SKS_PP_setup::find().RegMandateAutomation)
    {
        warning("@SKS:SKGLegalIDValidation"); //required field
        return false;
    }
    // SKS_EB_FDD-DD-NETS integration <--
    ret = super();

    if (ret)
    {
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Setup.AREMTemplateNoAttach, SKS_EFT_Setup.AREMTemplateLanguage) && ret;
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Setup.AREMTemplateAttach, SKS_EFT_Setup.AREMTemplateLanguage) && ret;
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Setup.ARAENTemplateNoAttach, SKS_EFT_Setup.AREMTemplateLanguage) && ret;
        ret = SKS_PP_Sys::ValidateEmailTemplate(SKS_EFT_Setup.ARAENTemplateAttach, SKS_EFT_Setup.AREMTemplateLanguage) && ret;
    }

    return ret;
}

