/// <summary>
/// validating company exists or not
/// </summary>
/// <returns>true or false</returns>
public boolean validate()
{
    DataArea    dataArea;
    container   companyList = SKS_EB_VirtualCompany::getDDMultiCompanies();
    int         value;
    boolean ret;
    ret = super();

    if(SKS_EFT_Setup.ARCreateSendCompany)
    {
        value  = conFind(companyList, SKS_EFT_Setup.ARCreateSendCompany);
        select firstonly Id from dataArea where dataArea.id == SKS_EFT_Setup.ARCreateSendCompany;
        if(!dataArea.id || !value)
        {
            ret = checkfailed("@SKS:SKGCompanyNotExists");
        }
    }

    return ret;
}

