public void modified()
{
    SKS_EFT_CustomerPaym sks_eft_CustomerPaym;
    SKS_EFT_FileFormat  sks_eft_fileformat;
    SKS_EFT_FileFormatPaymSpec  sks_eft_fileformatpaymspec;
    CustBankAccount custbankaccount;
    boolean showMessage = false;
    ;

    ttsbegin;

    if(!SKS_EFT_Setup.orig().ARReqPreNote && SKS_EFT_Setup.ARReqPreNote)
    {
        while select FormatId, PaymSpec, CustCompany, CustAccount, CustBankAccountId from sks_eft_CustomerPaym
        join prenote, formatid from sks_eft_fileformat
        where sks_eft_fileformat.FormatId == sks_eft_CustomerPaym.FormatId
            && sks_eft_fileformat.PreNote == NoYes::Yes
        join prenote, formatid, paymspec from sks_eft_fileformatpaymspec
        where  sks_eft_fileformatpaymspec.FormatId == sks_eft_fileformat.FormatId
            && sks_eft_fileformatpaymspec.PaymSpec == sks_eft_customerpaym.PaymSpec
            && sks_eft_fileformatpaymspec.PreNote == NoYes::Yes
        {
            changeCompany(sks_eft_CustomerPaym.CustCompany)
            {
                custBankAccount = null;

                select firstonly forupdate custbankaccount where custbankaccount.AccountID == sks_eft_CustomerPaym.CustBankAccountId
                    && custbankaccount.CustAccount == sks_eft_CustomerPaym.CustAccount
                    && custbankaccount.SKS_EFT_PreNoteStatus == SKS_EB_PreNoteStatus::NotApplicable;

                if(custbankaccount)
                {
                    custbankaccount.SKS_EFT_PreNoteStatus = SKS_EB_PreNoteStatus::New;
                    custbankaccount.SKS_EFT_PreNoteDate = dateNull();
                    custbankaccount.update();
                    showMessage = true;
                }
            }
        }
        if(showMessage)
        {
            info("@SKS1871");
        }
    }
    super();

    ttscommit;
}

