protected void initialize()
{
    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly StorageOption_APEFTFXDD from sks_EB_FileStorage;
    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        SKS_EFT_Setup_ARFileNameCanChange.visible(false);
    }
    else
    {
        SKS_EFT_Setup_ARFileNameCanChange.visible(true);
    }
    super();
}

/// <summary>
///
/// </summary>
public void lookup()
{
    PmfTableLookupExtended  sysTableLookup;
    Query                   query;
    QueryBuildDataSource    queryBuildDataSource;
    Container               companyList = SKS_EB_VirtualCompany::getDDMultiCompanies();
    int                     i;
    SKS_EFT_TmpHierarchy    sks_eft_TmpHierarchy;

    for (i=1; i <= conLen(companyList); i++)
    {
        sks_eft_TmpHierarchy.DataArea = conPeek(companyList, i);
        sks_eft_TmpHierarchy.insert();
    }

    sysTableLookup     = PmfTableLookupExtended::newParameters(tablenum(SKS_EFT_TmpHierarchy),this);
    query = new Query();

    queryBuildDataSource = query.addDataSource(tablenum(SKS_EFT_TmpHierarchy));
    sysTableLookup.addLookupfield(fieldnum(SKS_EFT_TmpHierarchy, DataArea), true);

    sysTableLookup.parmQuery(query);
    // DataSource on form with populated data via .setTmpData():
    sysTableLookup.parmTmpBuffer(sks_eft_TmpHierarchy);
    sysTableLookup.performFormLookup();

}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validate()
{
    boolean ret;
    DialogButton diabut;

    ret = super();

    if(ret && !SKS_EFT_Setup.DDFormatValidMultiThread)
    {
        diabut = Box::yesNo(strFmtLB("@SKS:SKGFormatValidationMultiThreadOn"), DialogButton::No, "@SKS225");
        if(diabut == DialogButton::No)
        {
            ret = false;
        }
    }

    return ret;
}

/// <summary>
///
/// </summary>
protected void initialize()
{

    SKS_EB_FileStorage  sks_EB_FileStorage;

    //Bank Fabric
    select firstonly sks_EB_FileStorage;
    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        FileName.visible(false);
        SKS_EFT_Setup_ARFilePath.visible(false);
        SKS_EFT_Setup_ARPathArchive.visible(false);
    }
    else
    {
        SKS_EFT_Setup_ARBankFabricFileLocation.visible(false);
    }

    super();
}

