/// <summary>
///
/// </summary>
public void lookup()
{
    Query query = new Query();
    QueryBuildDataSource qbds;
    QueryBuildRange queryBuildRange;
    SysTableLookup sysTableLookup;
    SKS_BF_TmpLookupTable TmpFileLocations;

    TmpFileLocations = SKS_BF_TmpLookupTable::construct(SKS_BF_FileLocCategory::AREFT);

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),this);

    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));

    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));

    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable,Name));

    sysTableLookup.parmTmpBuffer(TmpFileLocations);

    sysTableLookup.performFormLookup();
    //super();
}

void archivecheckbox_chk()
{
    //if archive checkbox is clicked enable archive path, otherwise disable the path
    if( SKS_EFT_Setup_ARArchive.value())
    {
        SKS_EFT_Setup_ARPathArchive.enabled(true);
    }
    else
    {
        SKS_EFT_Setup_ARPathArchive.enabled(false);
    }
}

public void clicked()
{
    super();

    this.archivecheckbox_chk();
}

/// <summary>
///
/// </summary>
public void clicked()
{
    Dialog  dialog;
    DialogField dialogField;
    DialogField dialogField2;
    ;

    dialog = new Dialog("@SKS1617");
    //default to .csv files
    dialogfield = dialog.addField(extendedTypeStr(Email), "@SKS1618");
    dialogfield2 = dialog.addField(extendedTypeStr(Email), "@SYS53122");
    if (dialog.run())
    {
        if ((dialogfield.value() != '') && (dialogfield2.value() != ''))
        {
            SKS_EFT_SendMail::mailServerTest(dialogfield.value(),dialogfield2.value());
        }
        else
        {
            box::info("@SKS1527");
        }
    }
    else
    {
        return; // if they just cancelled the dialog, then no error.
    }

}

