[SuppressBPWarning('BPUpgradeCodeLateBoundCall', 'Required')]
public void lookup()
{
    /*SysTableLookup              sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_EFT_DirectDebitReportNamesTmp), this);
    Query                       query = new Query();
