/// <summary>
///
/// </summary>
/// <returns></returns>
public Common lookupReference()
{
    Batch   BatchJobID;
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(Batch), SKS_EFT_Setup_AutoSendBatchJobId, true);
    ;
    query.allowCrossCompany(true);

    // Add fields to be shown in the lookup form
    sysTableLookup.addLookupField(fieldNum(Batch, Caption));

    // Limit and arrange the data selection
    queryBuildDataSource = query.addDataSource(tableNum(Batch));
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(Batch, classnumber));
    queryBuildRange.value(int2str(classNum(SKS_DD_MandateAuto)));
    sysTableLookup.parmQuery(query);

    // Perform the lookup
    BatchJobID = sysTableLookup.performFormLookup();

    return BatchJobID;
}

/// <summary>
/// Code to execute on control change.
/// </summary>
/// <returns> returns true otherwise; false </returns>
public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (SKS_DD_MandateMOPUpdates.SKS_DD_MandatesCancelUpdates)
        {
            SKS_DD_MandateMOPUpdates_SKS_DD_MandatesPaymModeCancel.allowEdit(true);
            SKS_DD_MandateMOPUpdates_SKS_DD_MandatesPaymSpecCancel.allowEdit(true);
        }
        else
        {
            SKS_DD_MandateMOPUpdates.SKS_DD_MandatesPaymModeCancel = '';
            SKS_DD_MandateMOPUpdates.SKS_DD_MandatesPaymSpecCancel = '';

            SKS_DD_MandateMOPUpdates_SKS_DD_MandatesPaymModeCancel.allowEdit(false);
            SKS_DD_MandateMOPUpdates_SKS_DD_MandatesPaymSpecCancel.allowEdit(false);
        }
    }

    return ret;
}
