boolean AddendaInUse()
{
    SKS_EFT_FilePmtSelection    lSKS_EFT_FilePmtSelection;
    VendPaymModeTable           lVendPaymModeTable;
    SKS_EFT_FileFormatPaymSpec          lSKS_EFT_FileFormatPaymSpec;
    ;
    select firstonly FormatId, AddendaCodeId from lSKS_EFT_FileFormatPaymSpec where lSKS_EFT_FileFormatPaymSpec.AddendaCodeID == SKS_EFT_AddendaFormats.AddendaCodeID
    join SKS_EFT_FormatId, PaymMode from lVendPaymModeTable where lVendPaymModeTable.SKS_EFT_FormatId == lSKS_EFT_FileFormatPaymSpec.FormatId
    join PaymMode from lSKS_EFT_FilePmtSelection
        where lSKS_EFT_FilePmtSelection.PaymMode == lVendPaymModeTable.PaymMode &&
        lSKS_EFT_FilePmtSelection.Module == SKS_EFT_Module::AP;

    if(lSKS_EFT_FilePmtSelection)
    {
        error('Addenda ' + SKS_EFT_AddendaFormats.AddendaCodeID + ' is in use by EFT+.');
        return true;
    }
    return false;
}

void fieldEnableDisable()
{
    //Disable All fields except for ValueType
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustFieldRequired)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustomField)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, DateFormat)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, FixedValue)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, IncludeDecimal)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, MaxLength)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadCharacter)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadField)).enabled(false);
    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadSide)).enabled(false);

    //Hide/Disable the Fixed Value fields for SKS_EFT_AddendaFormats
    SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, IdentificationFixedValue)).enabled(false);
    SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, IdentificationFixedValue)).visible(false);
    SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, RecCompanyFixedValue)).enabled(false);
    SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, RecCompanyFixedValue)).visible(false);

    //Activate fields based on ValueType
    switch(SKS_EFT_AddendaFields.ValueType)
    {
        case SKS_EFT_AddendaValueType::Cus1:
        case SKS_EFT_AddendaValueType::Cus2:
        case SKS_EFT_AddendaValueType::Cus3:
        case SKS_EFT_AddendaValueType::Cus4:
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, MaxLength)).enabled(true);
            if(SKS_EFT_AddendaFields.MaxLength > 0)
            {
                SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadField)).enabled(true);
            }
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustFieldRequired)).enabled(true);
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustomField)).enabled(true);
            SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateClear;
            SKS_EFT_AddendaFields.FixedValue = '';

            break;
        case SKS_EFT_AddendaValueType::CusAmt1:
        case SKS_EFT_AddendaValueType::CusAmt2:
        case SKS_EFT_AddendaValueType::CusAmt3:
        case SKS_EFT_AddendaValueType::CusAmt4:
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, MaxLength)).enabled(true);
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, IncludeDecimal)).enabled(true);
            if(SKS_EFT_AddendaFields.MaxLength > 0)
            {
                SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadField)).enabled(true);
            }
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustFieldRequired)).enabled(true);
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustomField)).enabled(true);
            SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateClear;
            SKS_EFT_AddendaFields.FixedValue = '';

            break;
        case SKS_EFT_AddendaValueType::EffectiveDate:
        case SKS_EFT_AddendaValueType::InvoiceDate:
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, DateFormat)).enabled(true);
            if (SKS_EFT_AddendaFields.DateFormat == SKS_EFT_DateFormat::DateClear)
            {
                SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateYYMMDD;
            }
            SKS_EFT_AddendaFields.FixedValue = '';
            SKS_EFT_AddendaFields.CustomField = '';
            SKS_EFT_AddendaFields.CustFieldRequired = NoYes::No;
            SKS_EFT_AddendaFields.MaxLength = 0;
            SKS_EFT_AddendaFields.PadCharacter = SKS_PP_FillCharacter::Zero;
            SKS_EFT_AddendaFields.PadSide = SKS_PP_FillSide::Left;
            break;
        case SKS_EFT_AddendaValueType::CusDate1:
        case SKS_EFT_AddendaValueType::CusDate2:
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, DateFormat)).enabled(true);
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustomField)).enabled(true);
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, CustFieldRequired)).enabled(true);
            if (SKS_EFT_AddendaFields.DateFormat == SKS_EFT_DateFormat::DateClear)
            {
                SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateYYMMDD;
            }
            SKS_EFT_AddendaFields.FixedValue = '';
            SKS_EFT_AddendaFields.MaxLength = 0;
            SKS_EFT_AddendaFields.PadCharacter = SKS_PP_FillCharacter::Zero;
            SKS_EFT_AddendaFields.PadSide = SKS_PP_FillSide::Left;

            break;
        case SKS_EFT_AddendaValueType::Fixed:
        case SKS_EFT_AddendaValueType::FixedMM:
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, FixedValue)).enabled(true);
            SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateClear;
            SKS_EFT_AddendaFields.CustomField = '';
            SKS_EFT_AddendaFields.CustFieldRequired = NoYes::No;
            break;
        case SKS_EFT_AddendaValueType::InvoiceAmt:
        case SKS_EFT_AddendaValueType::InvoiceAmtPmtCur:
        case SKS_EFT_AddendaValueType::InvoiceOrigAmt:
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, MaxLength)).enabled(true);
            SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, IncludeDecimal)).enabled(true);
            if(SKS_EFT_AddendaFields.MaxLength > 0)
            {
                SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadField)).enabled(true);
            }
            SKS_EFT_AddendaFields.FixedValue = '';
            SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateClear;
            SKS_EFT_AddendaFields.FixedValue = '';
            SKS_EFT_AddendaFields.CustomField = '';
            SKS_EFT_AddendaFields.CustFieldRequired = NoYes::No;
            break;
        default:
            SKS_EFT_AddendaFields.FixedValue = '';
            SKS_EFT_AddendaFields.DateFormat = SKS_EFT_DateFormat::DateClear;
            SKS_EFT_AddendaFields.CustomField = '';
            SKS_EFT_AddendaFields.CustFieldRequired = NoYes::No;

            if(SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::NotUsed)
            {
                SKS_EFT_AddendaFields.MaxLength = 0;
                SKS_EFT_AddendaFields.PadCharacter = SKS_PP_FillCharacter::Zero;
                SKS_EFT_AddendaFields.PadSide = SKS_PP_FillSide::Left;
            }
            else
            {
                SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, MaxLength)).enabled(true);

                if(SKS_EFT_AddendaFields.MaxLength > 0)
                {
                    SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadField)).enabled(true);
                }
            }
            break;
    }

    if(SKS_EFT_AddendaFields.PadField)
    {
        SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadCharacter)).enabled(true);
        SKS_EFT_AddendaFields_ds.object(fieldnum(SKS_EFT_AddendaFields, PadSide)).enabled(true);
    }

    if(SKS_EFT_AddendaFormats.AccountIDOvr == SKS_EFT_AddendaOverwrite::Fixed)
    {
        SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, IdentificationFixedValue)).enabled(true);
        SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, IdentificationFixedValue)).visible(true);
    }

    if(SKS_EFT_AddendaFormats.RecCompany == SKS_EFT_AddendaOverwrite::Fixed)
    {
        SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, RecCompanyFixedValue)).enabled(true);
        SKS_EFT_AddendaFormats_ds.object(fieldnum(SKS_EFT_AddendaFormats, RecCompanyFixedValue)).visible(true);
    }
}

