boolean overwriteDefaultAddendaFields()
{
    SKS_EFT_AddendaFields   lSKS_EFT_AddendaFields;
    boolean ret = false;
    ;

    select firstOnly lSKS_EFT_AddendaFields where lSKS_EFT_AddendaFields.FieldNumber > 1 &&
                     lSKS_EFT_AddendaFields.ValueType != SKS_EFT_AddendaValueType::NotUsed &&
                     lSKS_EFT_AddendaFields.AddendaCodeID == SKS_EFT_AddendaFormats.AddendaCodeID;

    if(lSKS_EFT_AddendaFields)
    {
        if(Box::okCancel("@SKS2599", DialogButton::Cancel) == DialogButton::Ok)
        {
            ret = true;
        }
    }
    else
    {
        ret = true;
    }
    return ret;
}

void refreshForm()
{
    SKS_EFT_AddendaFormats_ds.research();
    SKS_EFT_AddendaFields_ds.research();
}

void resetFields()
{
    SKS_EFT_AddendaFields lSKS_EFT_AddendaFields;
    int x;
    int maxRec = 10;
    RecordInsertList    ril;
    ;
    ril = new RecordInsertList(lSKS_EFT_AddendaFields.TableId);
    delete_from lSKS_EFT_AddendaFields where lSKS_EFT_AddendaFields.AddendaCodeID == SKS_EFT_AddendaFormats.AddendaCodeID;
    select lSKS_EFT_AddendaFields where lSKS_EFT_AddendaFields.AddendaCodeID == SKS_EFT_AddendaFormats.AddendaCodeID;

    if(SKS_EFT_AddendaFormats.AddendaType == SKS_EFT_AddendaFormatType::ChildSupPaym)
    {
        maxRec = 9;
    }
    else if(SKS_EFT_AddendaFormats.AddendaType == SKS_EFT_AddendaFormatType::Custom)
    {
        maxRec = 1;
    }

    if(!lSKS_EFT_AddendaFields)
    {
        for(x = 1; x <= maxRec; x++)
        {
            if(x == 1)
            {
                if(maxRec == 10)
                {
                    lSKS_EFT_AddendaFields.ValueType = SKS_EFT_AddendaValueType::Fixed;
                    lSKS_EFT_AddendaFields.FixedValue = 'TXP';
                }
                else if(Maxrec == 1)
                {
                    lSKS_EFT_AddendaFields.ValueType = SKS_EFT_AddendaValueType::Fixed;
                    lSKS_EFT_AddendaFields.FixedValue = 'NTE';
                }
                else
                {
                    lSKS_EFT_AddendaFields.ValueType = SKS_EFT_AddendaValueType::Fixed;
                    lSKS_EFT_AddendaFields.FixedValue = 'DED';
                }
            }
            else
            {
                lSKS_EFT_AddendaFields.ValueType = SKS_EFT_AddendaValueType::NotUsed;
                lSKS_EFT_AddendaFields.FixedValue = '';
            }

            lSKS_EFT_AddendaFields.AddendaCodeID = SKS_EFT_AddendaFormats.AddendaCodeID;
            lSKS_EFT_AddendaFields.FieldNumber = x;
            ril.add(lSKS_EFT_AddendaFields);
        }
        ril.insertDatabase();
    }
}

void setFieldNumbers()
{
    SKS_EFT_AddendaFields lSKS_EFT_AddendaFields;
    int x = 1;
    ;

    select forUpdate lSKS_EFT_AddendaFields Order by FieldNumber where lSKS_EFT_AddendaFields.AddendaCodeID == SKS_EFT_AddendaFormats.AddendaCodeID;

    ttsBegin;
    while(lSKS_EFT_AddendaFields)
    {
        lSKS_EFT_AddendaFields.FieldNumber = x;
        x++;
        lSKS_EFT_AddendaFields.update();
        next lSKS_EFT_AddendaFields;
    }
    ttsCommit;
}

public boolean validateWrite()
{
    boolean ret;

    if(SKS_EFT_AddendaFields.CustomField == '' &&
      (SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::Cus1 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::Cus2 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::Cus3 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::Cus4 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::CusAmt1 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::CusAmt2 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::CusAmt3 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::CusAmt4 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::CusDate1 ||
       SKS_EFT_AddendaFields.ValueType == SKS_EFT_AddendaValueType::CusDate2))
    {
        warning("@SKS2606");
        ret = false;
    }
    else
    {
        ret = super();
    }

    return ret;
}

public void create(boolean _append = false)
{
    SKS_EFT_AddendaFields  lSKS_EFT_AddendaFields;
    int nextNumber;
    ;
    super(_append);
    select maxOf(fieldNumber) from lSKS_EFT_AddendaFields where lSKS_EFT_AddendaFields.AddendaCodeID == SKS_EFT_AddendaFormats.AddendaCodeID;

    nextNumber = lSKS_EFT_AddendaFields.FieldNumber + 1;
    SKS_EFT_AddendaFields.FieldNumber = nextNumber;
    SKS_EFT_AddendaFields.AddendaCodeID = SKS_EFT_AddendaFormats.AddendaCodeID;
    SKS_EFT_AddendaFields.ValueType = SKS_EFT_AddendaValueType::NotUsed;
    this.last();
}

