void clicked()
{
    SKS_EFT_AddendaFormats tmpSKS_EFT_AddendaFormats, selectSKS_EFT_AddendaFormats;
    SKS_EFT_AddendaFields tmpSKS_EFT_AddendaFields, selectSKS_EFT_AddendaFields;
    SKS_EFT_AddendaNotes tmpSKS_EFT_AddendaNotes, selectSKS_EFT_AddendaNotes;
    SKS_EFT_AddendaCodeID lSKS_EFT_AddendaCodeID;

    CommaStreamIo inFile;
    Filename    filename;
    Container   line;
    int fieldNumber;
    int totaladds;
    str hdrgrpdet;

    super();

    SKS_FileUploadDialogForm fileUploadDialog = new SKS_FileUploadDialogForm();
    fileUploadDialog.parmFileDialogCaptionForForm("@SKS2525");
    fileUploadDialog.parmFileTypesAccepted('.csv');
    fileUploadDialog.parmStrategyClassName(classstr(SKS_PP_CSVFileTemporaryStorageStrategy));
    FileUploadTemporaryStorageResult fileUploadResult = fileUploadDialog.getFileUploadResult();
    if (fileUploadResult != null && fileUploadResult.getUploadStatus())
    {
        filename = fileUploadResult.getDownloadUrl();
        System.IO.Stream readStream = fileUploadResult.openResult(); // This is used to upload a .csv so it doesn't need to use FileHub
        inFile = CommaStreamIo::constructForRead(readStream);
    }
    else
    {
        return;
    }

    if (!inFile || infile.status() != IO_Status::Ok )
    {
        //file could not be opened
        throw error (strfmt("@SYS19312", filename));
    }

    ttsbegin;

    infile.inRecordDelimiter('\r\n');
    infile.inFieldDelimiter(',');

    totaladds = 0;


    //Checking status of last operation..
    while (infile.status() == IO_status::Ok)
    {
        line = infile.read();
        if (line)
        {
            hdrgrpdet = conpeek(line, 1);
            if (hdrgrpdet == 'H') //Header
            {
                tmpSKS_EFT_AddendaFormats.initValue();
                lSKS_EFT_AddendaCodeID = conpeek(line, 2); //Addenda Code
                tmpSKS_EFT_AddendaFormats.AddendaCodeID = lSKS_EFT_AddendaCodeID;
                tmpSKS_EFT_AddendaFormats.AddendaType = conpeek(line, 3); //Addenda Type
                tmpSKS_EFT_AddendaFormats.Description = conpeek(line, 4); //Description
                tmpSKS_EFT_AddendaFormats.CompanyName = conpeek(line, 5); //Company Name
                tmpSKS_EFT_AddendaFormats.CompanyDiscretionaryData = conpeek(line, 6); //Company Discretionary Data
                tmpSKS_EFT_AddendaFormats.CompanyID = conpeek(line, 7); //Company ID override
                tmpSKS_EFT_AddendaFormats.CompanyDescription = conpeek(line, 8); //Company Description override
                tmpSKS_EFT_AddendaFormats.AccountIDOvr = conpeek(line, 9); //Account id override
                tmpSKS_EFT_AddendaFormats.IdentificationFixedValue = conpeek(line, 10); //Identification override fixed value
                tmpSKS_EFT_AddendaFormats.RecCompany = conpeek(line, 11); //Recieving Company override
                tmpSKS_EFT_AddendaFormats.RecCompanyFixedValue = conpeek(line, 12); //Recieving Company override fixed value

                //increment only if record did not exists
                select firstonly selectSKS_EFT_AddendaFormats where selectSKS_EFT_AddendaFormats.AddendaCodeID == lSKS_EFT_AddendaCodeID;
                if(!selectSKS_EFT_AddendaFormats)
                {
                    tmpSKS_EFT_AddendaFormats.insert();
                    totaladds++;
                }
            }
            else if (hdrgrpdet == 'D') //Addenda Fields
            {
                tmpSKS_EFT_AddendaFields.initValue();
                lSKS_EFT_AddendaCodeID = conpeek(line, 2);
                fieldNumber = conpeek(line, 3);

                tmpSKS_EFT_AddendaFields.AddendaCodeID = lSKS_EFT_AddendaCodeID;         //Addenda Code ID
                tmpSKS_EFT_AddendaFields.FieldNumber = fieldNumber;          //Field Number
                tmpSKS_EFT_AddendaFields.Description = conpeek(line, 4);          //Field Description
                tmpSKS_EFT_AddendaFields.ValueType = conpeek(line, 5);            //Value Type
                tmpSKS_EFT_AddendaFields.FixedValue = conpeek(line, 6);           //Fixed Value
                tmpSKS_EFT_AddendaFields.MaxLength = conpeek(line, 7);            //Maximum length
                tmpSKS_EFT_AddendaFields.PadField = conpeek(line, 8);             //Pad Field
                tmpSKS_EFT_AddendaFields.PadCharacter = conpeek(line, 9);         //Pad character
                tmpSKS_EFT_AddendaFields.PadSide = conpeek(line, 10);              //Pad side
                tmpSKS_EFT_AddendaFields.DateFormat = conpeek(line, 11);          //Date format
                tmpSKS_EFT_AddendaFields.CustomField = conpeek(line, 12);         //Custom field
                tmpSKS_EFT_AddendaFields.CustFieldRequired = conpeek(line, 13);   //Require Custom field

                //Check if Field exists
                select firstonly selectSKS_EFT_AddendaFields where selectSKS_EFT_AddendaFields.FieldNumber == fieldNumber &&
                                 selectSKS_EFT_AddendaFields.AddendaCodeID == lSKS_EFT_AddendaCodeID;
                if(!selectSKS_EFT_AddendaFields)
                {
                    tmpSKS_EFT_AddendaFields.insert();
                    totaladds++;
                }
            }
            else if (hdrgrpdet == 'N') //Addenda Notes
            {
                tmpSKS_EFT_AddendaNotes.initValue();
                lSKS_EFT_AddendaCodeID = conpeek(line, 2);
                tmpSKS_EFT_AddendaNotes.AddendaCodeID = lSKS_EFT_AddendaCodeID; //Addenda Code ID
                tmpSKS_EFT_AddendaNotes.UserNote = conpeek(line, 3);            //User Note

                //Check if Note exists
                select firstonly selectSKS_EFT_AddendaNotes where selectSKS_EFT_AddendaNotes.AddendaCodeID == lSKS_EFT_AddendaCodeID;
                if(!selectSKS_EFT_AddendaNotes)
                {
                    tmpSKS_EFT_AddendaNotes.insert();
                    totaladds++;
                }
            }
        }
    }
    ttscommit;
    inFile = null;
    //report total adds
    info("@SYS54695" + ' ' + "@SYS108583" + ' :' + int2str(totaladds));
    element.refreshForm();
}
