void BankFabricFieldsInit()
{
    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly sks_EB_FileStorage;
    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        SKS_EFTBB_Bank_BBFileName.visible(false);
        SKS_EFTBB_Bank_BBFilePath.visible(false);
        SKS_EFTBB_Bank_ControlFileName.visible(false);
        SKS_EFTBB_Bank_BBPathArchive.visible(false);
        SKS_EFTBB_Bank_InboundFileLoc.visible(false);
    }
    else
    {
        SKS_EFTBTB_Bank_BTBBankFabricFileLocation.visible(false);
        SKS_EFTBTB_Bank_BTBBankFabricFileNames.visible(false);
        SKS_EFTBTB_Bank_BTBBankFabricInboundFileLocation.visible(false);
    }
}

void BankFabricTableLookup(Args _args, SKS_BF_FileLocCategory _category,SKS_BF_LookupTable _lookuptable)
{
    SKS_BF_TmpLookupTable TmpFileLocations;
    Query query = new Query();

    QueryBuildDataSource qbds;

    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup;

    TmpFileLocations = SKS_BF_TmpLookupTable::construct(_category,_lookuptable);

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),_args.parmObject());

    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));

    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));

    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable,Name));

    sysTableLookup.parmTmpBuffer(TmpFileLocations);

    sysTableLookup.performFormLookup();
}

public void enabledisablecontrolfile()
{
    if(SKS_EFTBTB_Bank.BBControlFileFormatiD == '')
    {
        SKS_EFTBTB_Bank.BBControlFileName = '';
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BBControlFileName)).allowEdit(false);
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BTBBankFabricFileNames)).allowEdit(false);
    }
    else
    {
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BBControlFileName)).allowEdit(true);
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BTBBankFabricFileNames)).allowEdit(true);
    }
}

public void enableDisableInboundInfo()
{
    SKS_BTB_PaymModeTable   lSKS_BTB_PaymMode;
    sks_eft_fileformat  lSKS_EFT_FileFormat;

    select firstonly lSKS_BTB_PaymMode
            where lSKS_BTB_PaymMode.PaymMode == SKS_EFTBTB_Bank.PaymMode
        join
            lSKS_EFT_FileFormat where lSKS_EFT_FileFormat.FormatId == lSKS_BTB_PaymMode.FormatId;

    if(lSKS_EFT_FileFormat.SKS_BTB_InboundFileGroupId == '')
    {
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BTBInboundFileLoc)).allowedit(false);
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, InboundCommID)).allowedit(false);
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BTBBankFabricInboundFileLocation)).allowedit(false);
    }
    else
    {
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BTBInboundFileLoc)).allowedit(true);
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, InboundCommID)).allowedit(true);
        SKS_EFTBTB_Bank_ds.object(fieldNum(SKS_EFTBTB_Bank, BTBBankFabricInboundFileLocation)).allowedit(true);
    }
}

public void init()
{
    SKS_PP_Sys tmpSKS_PP_Sys;
    ;

    super();
    tmpSKS_PP_Sys = new SKS_PP_Sys();

    select mastacctlist from sks_eft_fileformat;

    if ( !tmpSKS_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
    else
    {
        if (!element.args().caller())
        {
            throw error("@SYS96590");
        }

        select * from sks_eft_setup;
    }

    if(sks_eft_setup.APAcctApproval == NoYes::No)
    {
        BankAccountSrc_SKS_EFT_AcctStatus.visible(false);
        BankAccountSrc_SKS_EFT_AcctModifier.visible(false);
        BankAccountSrc_SKS_EFT_AcctModifiedDate.visible(false);
        BankAccountSrc_SKS_EFT_AcctApprover.visible(false);
        BankAccountSrc_SKS_EFT_AcctApprovalDate.visible(false);
    }
    else
    {
        BankAccountSrc_SKS_EFT_AcctStatus.visible(true);
        BankAccountSrc_SKS_EFT_AcctModifier.visible(true);
        BankAccountSrc_SKS_EFT_AcctModifiedDate.visible(true);
        BankAccountSrc_SKS_EFT_AcctApprover.visible(true);
        BankAccountSrc_SKS_EFT_AcctApprovalDate.visible(true);
    }

  //  AttachmentPrompt.text("@SKS:SKGBankAttachName");
    IsVisibleValue.text("@SKS2850");
}

