void loadSKS_EFT_FileFormat()
{
    ;

    if(SKS_EFTBTB_Bank.PaymMode != SKS_BTB_PaymMode.PaymMode)
    {
        select FormatID from SKS_BTB_PaymMode
            where SKS_BTB_PaymMode.PaymMode == SKS_EFTBTB_Bank.PaymMode
        join
            sks_eft_fileFormat where sks_eft_fileFormat.FormatId == SKS_BTB_PaymMode.FormatId;
    }
}

public boolean MastAcctListEnabled(PaymMode _paymMode)
{
    SKS_BTB_PaymModeTable  lSKS_BTB_PaymMode;
    SKS_EFT_FileFormat lSKS_EFT_FileFormat;
    ;

    select firstonly FormatId from lSKS_BTB_PaymMode where lSKS_BTB_PaymMode.paymmode == _paymMode;
    select firstOnly MastAcctList from lSKS_EFT_FileFormat where lSKS_EFT_FileFormat.FormatId == lSKS_BTB_PaymMode.FormatId;

    return lSKS_EFT_FileFormat.MastAcctList;
}

void setMaskAndLengthValues()
{
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspec;
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspecCpny;
    Int   CpnyNM;
    ;

    if(SKS_BTB_PaymMode && sks_eft_fileformat)
    {
        //Company Info
        CpnyNm = 0;
        Select firstonly FormatID from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == SKS_BTB_PaymMode.FormatId && sks_eft_Fileformatpaymspec.TxnNacha == NoYes::Yes &&
            sks_eft_Fileformatpaymspec.Selected == NoYes::Yes;
        Select firstonly FormatID from sks_eft_fileformatpaymspecCpny
            where sks_eft_fileformatpaymspecCpny.formatid == SKS_BTB_PaymMode.FormatId && sks_eft_fileformatpaymspecCpny.CpnyIDNameReq == NoYes::Yes &&
            sks_eft_fileformatpaymspecCpny.Selected == NoYes::Yes;

        //Display CompanyID
        If(sks_eft_fileformat.CompanyIdTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFTBB_Bank_ACHCompanyID.label(sks_eft_fileformat.CompanyIdTitle);
            element.setProperties(sks_eft_fileformat.CompanyIdMask, SKS_EFTBB_Bank_ACHCompanyID);
            SKS_EFTBB_Bank_ACHCompanyID.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFTBB_Bank_ACHCompanyID.visible(False);
        }
        //Display Company Name
        IF(sks_eft_fileformat.CompanyNameTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFTBB_Bank_ACHCompanyName.label(sks_eft_fileformat.CompanyNameTitle);
            element.setProperties(sks_eft_fileformat.CompanyNameMask, SKS_EFTBB_Bank_ACHCompanyName);
            SKS_EFTBB_Bank_ACHCompanyName.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFTBB_Bank_ACHCompanyName.visible(False);
        }

        If (!CpnyNm)
        {
            SKS_EFTBB_Bank_CompanyIDUnique.visible(False);
            SKS_EFTBB_Bank_Companyidppd.visible(false);
            SKS_EFTBB_Bank_CompanyNamePPD.visible(false);
            SKS_EFTBB_Bank_CompanyidCCD.visible(false);
            SKS_EFTBB_Bank_CompanyNameCCD.visible(false);
            SKS_EFTBB_Bank_CompanyIDIAT.visible(false);
            SKS_EFTBB_Bank_CompanyNameIAT.visible(false);
            SKS_EFTBB_Bank_CompanyNameCTX.visible(false);
            SKS_EFTBB_Bank_CompanyIDCTX.visible(false);

        }
        else
        {
            SKS_EFTBB_Bank_CompanyIDUnique.visible(True);
            SKS_EFTBB_Bank_CompanyIdUnique.visible(true);
            SKS_EFTBB_Bank_Companyidppd.visible(true);
            SKS_EFTBB_Bank_CompanyNamePPD.visible(true);
            SKS_EFTBB_Bank_CompanyidCCD.visible(true);
            SKS_EFTBB_Bank_CompanyNameCCD.visible(true);
            SKS_EFTBB_Bank_CompanyIDIAT.visible(true);
            SKS_EFTBB_Bank_CompanyNameIAT.visible(true);
            SKS_EFTBB_Bank_CompanyNameCTX.visible(true);
            SKS_EFTBB_Bank_CompanyIDCTX.visible(true);

            If(SKS_EFTBTB_Bank.CompanyIDUnique==True)
            {
                SKS_EFTBB_Bank_CompanyIDPPD.enabled(True);
                SKS_EFTBB_Bank_CompanyIDCCD.enabled(True);
                SKS_EFTBB_Bank_CompanyIDIAT.enabled(True);
                SKS_EFTBB_Bank_CompanyIDCTX.enabled(True);
                SKS_EFTBB_Bank_CompanyNamePPD.enabled(True);
                SKS_EFTBB_Bank_CompanyNameCCD.enabled(True);
                SKS_EFTBB_Bank_CompanyNameIAT.enabled(True);
                SKS_EFTBB_Bank_CompanyNameCTX.enabled(True);
            }
            else
            {
                SKS_EFTBB_Bank_CompanyIDPPD.enabled(False);
                SKS_EFTBB_Bank_CompanyIDCCD.enabled(False);
                SKS_EFTBB_Bank_CompanyIDIAT.enabled(False);
                SKS_EFTBB_Bank_CompanyIDCTX.enabled(False);
                SKS_EFTBB_Bank_CompanyNamePPD.enabled(False);
                SKS_EFTBB_Bank_CompanyNameCCD.enabled(False);
                SKS_EFTBB_Bank_CompanyNameIAT.enabled(False);
                SKS_EFTBB_Bank_CompanyNameCTX.enabled(False);
            }
        }

        if(sks_eft_fileformat.ImmDestNameTitle)
        {
            //Immediate destination
            SKS_EFTBB_Bank_ACHImmDest.label(sks_eft_fileformat.ImmDestTitle);
            element.setProperties(sks_eft_fileformat.ImmDestMask, SKS_EFTBB_Bank_ACHImmDest);

            SKS_EFTBB_Bank_ACHImmDestName.label(sks_eft_fileformat.ImmDestNameTitle);
            element.setProperties(sks_eft_fileformat.ImmDestNameMask, SKS_EFTBB_Bank_ACHImmDestName);
            SKS_EFTBB_Bank_ACHImmDestName.visible(True);
        }
        else
        {
            SKS_EFTBB_Bank_ACHImmDestName.visible(False);
        }

        IF(sks_eft_fileformat.ImmOriginNameTitle)
        {
            //Immediate originator
            SKS_EFTBB_Bank_ACHImmOrig.label(sks_eft_fileformat.ImmOriginTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginMask, SKS_EFTBB_Bank_ACHImmOrig);

            SKS_EFTBB_Bank_ACHImmOrigName.label(sks_eft_fileformat.ImmOriginNameTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginNameMask, SKS_EFTBB_Bank_ACHImmOrigName);
            SKS_EFTBB_Bank_ACHImmOrigName.visible(True);
        }
        else
        {
            SKS_EFTBB_Bank_ACHImmOrigName.visible(False);
        }

        /*
        //Wire Plus values
        SKS_EFTBB_Bank_W01.label(sks_eft_fileformat.WBank01Description);
        element.setProperties(sks_eft_fileformat.WBank01Mask, SKS_EFTBB_Bank_W01);

        SKS_EFTBB_Bank_W02.label(sks_eft_fileformat.WBank02Description);
        element.setProperties(sks_eft_fileformat.WBank02Mask, SKS_EFTBB_Bank_W02);

        SKS_EFTBB_Bank_W03.label(sks_eft_fileformat.WBank03Description);
        element.setProperties(sks_eft_fileformat.WBank03Mask, SKS_EFTBB_Bank_W03);

        SKS_EFTBB_Bank_W04.label(sks_eft_fileformat.WBank04Description);
        element.setProperties(sks_eft_fileformat.WBank04Mask, SKS_EFTBB_Bank_W04);*/
    }
}

void setProperties(str _maskValue, FormStringControl _formControl)
{
    ;

    if(_maskValue)
    {
        _formControl.visible(true);

        if (substr(_maskValue, 0, 1) == 'U')
        {
            _formControl.changeCase(2);
        }

        _formControl.limitText(strLen(_maskValue));
    }
    else
    {
        _formControl.visible(false);
        _formControl.changeCase(0);
        _formControl.limitText(0, AutoMode::Auto);
    }
}

void SKS_setEFTTab()
{
    if(sks_eft_setup.APAcctApproval)
    {
        IsVisibleValue.visible(false);
    }
    else
    {
        IsVisibleValue.visible(true);
    }
}

