{
    boolean enabledProperty;
    ;

    if (SKS_EFTBTB_Bank.BB_EMFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }
    SKS_EFTBB_Bank_BBContactName.enabled(enabledProperty);
    SKS_EFTBB_Bank_BBContactPhone.enabled(enabledProperty);
    SKS_EFTBB_Bank_BBContactEmail.enabled(enabledProperty);
}

