public void create(boolean _append = false)
{
    super(_append);

    SKS_EFTBTB_Bank.CompanyFromSetup = 1;
    SKS_EFTBTB_Bank.FileFromSetup = 1;
    SKS_EFTBTB_Bank.BBFileFromSetup = 1;
    SKS_EFTBTB_Bank.BBEMFromSetup = 1;
    SKS_EFTBTB_Bank.PostedPaymSetupDefaults = 1;

    SKS_EFTBTB_Bank.BB_EMFromSetup = NoYes::Yes;
    element.sks_APLoadInternalContactInfoFromSetup();
    element.loadCompanyInfoFromSetup();
    element.loadFileHeaderFromSetup();
    element.loadFileInfoFromSetup();
}

public boolean validateWrite()
{
    boolean ret;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFTBTB_Bank_BTBBankFabricFileLocation.valueStr())
        {
            warning(strFmt("@SKS2643","@SKS:SKGBFBTBFileLocation")); //required field

            SKS_EFTBTB_Bank_BTBBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {

        if(!SKS_EFTBB_Bank_BBFileName.valueStr())
        {
            warning(strFmt("@SKS2643","@SKS1237")); //required field

            return false;
        }

        if(!SKS_EFTBB_Bank_BBFilePath.valueStr())
        {
            warning(strFmt("@SKS2643","@SKS1246")); //required field

            return false;
        }

        if(SKS_EFTBTB_Bank.BBArchive && !SKS_EFTBTB_Bank.BBPathArchive && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            warning(strFmt("@SKS2643","@SKS75")); //required field

            return false;
        }
    }

    if(SKS_EFTBTB_Bank.BTBCommID == '')
    {
        warning(strFmt("@SKS2643","@SKS2444")); //required field

        return false;
    }

    if(SKS_EFTBTB_Bank.InboundCommID != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFTBTB_Bank.BTBBankFabricInboundFileLocation == '')
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFInbFileLocation"));

                return false;
            }
        }
        else
        {
            if(SKS_EFTBTB_Bank.BTBInboundFileLoc == '')
            {
                warning(strFmt("@SKS2643","@SKS2716"));

                return false;
            }
        }
    }

    if(SKS_EFTBTB_Bank.BBControlFileFormatiD != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFTBTB_Bank.BTBBankFabricFileNames == '')
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFFileNames")); //required field

                return false;
            }
        }
        else
        {

            if(SKS_EFTBTB_Bank.BBControlFileName == '')
            {
                warning(strFmt("@SKS2643","@SKS3242")); //required field

                return false;
            }
        }
    }
    ret = super();
    ret = SKS_EFTBTB_Bank.SKS_DD_FileLocationValide(SKS_EFTBTB_Bank);

    //SKS_FDD_16447/16448--->
    if(ret)
    {
        SKS_EFT_FileFormatFields            lSKS_EFT_FileFormatFields;
        SKS_EFT_BTBBankAccountPaymLookup     lSKS_EFT_BankAccountPaymLookup;

        while select required from lSKS_EFT_FileFormatFields join lSKS_EFT_BankAccountPaymLookup
            where lSKS_EFT_BankAccountPaymLookup.FileFormatFieldsRecId == lSKS_EFT_FileFormatFields.recid &&
            lSKS_EFT_BankAccountPaymLookup.BankPaymRecId == SKS_EFTBTB_Bank.RecId
            && lSKS_EFT_FileFormatFields.Required == NoYes::Yes
        {
            if(lSKS_EFT_BankAccountPaymLookup.fixedvalue == '' && lSKS_EFT_BankAccountPaymLookup.CodeLookupValuesRecId == 0)
            {
                error("@SKS2682");
                return false;
            }
        }
    }
    //SKS_FDD_16447/16448<---

    return ret;
}

public int active()
{
    int ret;

    ret = super();


    element.loadSKS_EFT_FileFormat();

    element.disableCompanyInfo();
    element.disableFileHeader();
    element.disableFileInfo();
    element.sks_APdisableInternalContact();
    element.disableWireIAT();

    element.setMaskAndLengthValues();
    SKS_EFTBTB_Bank_ds.object(fieldnum(SKS_EFTBTB_Bank, BBPathARchive)).enabled(SKS_EFTBTB_Bank.BBArchive);
    element.enableDisableInboundInfo();
    element.enableDisableControlFile();

    if(SKS_EFTBTB_Bank.CompanyFromSetup == NoYes::Yes)
    {
        element.loadCompanyInfoFromSetup();
    }

    if(SKS_EFTBTB_Bank.BBFileFromSetup == NoYes::Yes)
    {
        element.loadFileInfoFromSetup();
    }
    return ret;
}

/// <summary>
/// Get the BTB transferType
/// </summary>
/// <returns>
/// transferType
/// </returns> 
///<param>SKS_EFTBTB_Bank
 ///</param>

public display SKS_BTB_TransferType transferType(SKS_EFTBTB_Bank _SKS_EFTBTB_Bank)
{
    SKS_EFTBTB_Bank lSKS_EFTBTB_Bank;

    select firstonly lSKS_EFTBTB_Bank where lSKS_EFTBTB_Bank.RecId == _SKS_EFTBTB_Bank.RecId;

    return (SKS_BTB_PaymModeTable::find(lSKS_EFTBTB_Bank.PaymMode).TransferType);
}

