void disableCompanyInfo()
{
    boolean enabledProperty;
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspec;
    ;

    if (SKS_EFTCNP_Bank.CompanyFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }

    SKS_EFTCNP_Bank_ACHCompanyID.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHCompanyName.enabled(enabledProperty);
    Select firstonly FormatID from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == custpaymmodetable.SKS_CNP_FormatId && SKS_eft_Fileformatpaymspec.TxnNacha == NoYes::Yes &&
            sks_eft_fileformatpaymspec.Selected == NoYes::Yes;
    If (sks_eft_fileformatpaymspec)
    {
        SKS_EFTCNP_Bank_ACHOrigDFI.visible(True);
        SKS_EFTCNP_Bank_ACHOrigDFIName.visible(True);

    }
    else
    {
        SKS_EFTCNP_Bank_ACHOrigDFI.visible(False);
        SKS_EFTCNP_Bank_ACHOrigDFIName.visible(False);
    }
    RefreshEFTFromSetup.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHOrigDFI.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHOrigDFIName.enabled(enabledProperty);

}

