void disableFileHeader()
{

    boolean enabledProperty;
    ;

    if (SKS_EFTCNP_Bank.FileFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }

    SKS_EFTCNP_Bank_ACHImmDest.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHImmDestName.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHImmOrig.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHImmOrigName.enabled(enabledProperty);
    SKS_EFTCNP_Bank_ACHFileIDModifier.enabled(enabledProperty);
    SKS_EFTCNP_Bank_FillBlock.enabled(enabledProperty);
    RefreshFromSetup.enabled(enabledProperty);

    // Hide options if off in file format
    if(sks_eft_fileFormat.BlockFill)
    {
        SKS_EFTCNP_Bank_FillBlock.visible(true);
        SKS_EFTCNP_Bank_FillBlock.enabled(enabledProperty);
    }
    else
    {
        SKS_EFTCNP_Bank_FillBlock.visible(false);
    }

    if(sks_eft_fileFormat.CrLf)
    {
        SKS_EFTCNP_Bank_FileCrLf.visible(true);
        SKS_EFTCNP_Bank_FileCrLf.enabled(enabledProperty);
    }
    else
    {
        SKS_EFTCNP_Bank_FileCrLf.visible(false);
    }
}

void disableFileInfo()
{
