void BankFabricFieldsInit()
{
    SKS_EB_FileStorage  sks_EB_FileStorage;

    //BankFabric
    select firstonly sks_EB_FileStorage;
    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        SKS_EFTCNP_Bank_CNPFileName.visible(false);
        SKS_EFTCNP_Bank_CNPFilePath.visible(false);
        SKS_EFTCNP_Bank_ControlFileName.visible(false);
        SKS_EFTCNP_Bank_CNPPathArchive.visible(false);
        SKS_EFTCNP_Bank_InboundFileLoc.visible(false);
    }
    else
    {
        SKS_EFTCNP_Bank_CNPBankFabricFileLocation.visible(false);
        SKS_EFTCNP_Bank_CNPBankFabricFileNames.visible(false);
        SKS_EFTCNP_Bank_CNPBankFabricInboundFileLocation.visible(false);
    }
}

public void enabledisablecontrolfile()
{
    if(SKS_EFTCNP_Bank.CNPControlFileFormatiD == '')
    {
        SKS_EFTCNP_Bank.CNPControlFileName = '';
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPControlFileName)).allowEdit(false);
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPBankFabricFileNames)).allowEdit(false);
    }
    else
    {
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPControlFileName)).allowEdit(true);
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPBankFabricFileNames)).allowEdit(true);
    }
}

public void enableDisableInboundInfo()
{
    CustPaymModeTable   lCustPaymModeTable;
    sks_eft_fileformat  lSKS_EFT_FileFormat;

    select firstonly lCustPaymModeTable
            where lCustPaymModeTable.PaymMode == SKS_EFTCNP_Bank.PaymMode
        join
            lSKS_EFT_FileFormat where lSKS_EFT_FileFormat.FormatId == lCustPaymModeTable.SKS_CNP_FormatId;

    if(lSKS_EFT_FileFormat.SKS_CNP_InboundFileGroupId == '')
    {
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPInboundFileLoc)).allowedit(false);
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, InboundCommID)).allowedit(false);
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPBankFabricInboundFileLocation)).allowedit(false);
    }
    else
    {
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPInboundFileLoc)).allowedit(true);
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, InboundCommID)).allowedit(true);
        SKS_EFTCNP_Bank_ds.object(fieldNum(SKS_EFTCNP_Bank, CNPBankFabricInboundFileLocation)).allowedit(true);
    }
}

public void init()
{
    SKS_PP_Sys tmpSKS_PP_Sys;
    ;

    super();
    tmpSKS_PP_Sys = new SKS_PP_Sys();

    select mastacctlist from sks_eft_fileformat;

    if ( !tmpSKS_PP_Sys.ValidateSetup(this.name()))
    {
        this.close();
    }
    else
    {
        if (!element.args().caller())
        {
            throw error("@SYS96590");
        }

        select * from sks_eft_setup;
    }

    if(sks_eft_setup.APAcctApproval == NoYes::No)
    {
        BankAccountSrc_SKS_EFT_AcctStatus.visible(false);
        BankAccountSrc_SKS_EFT_AcctModifier.visible(false);
        BankAccountSrc_SKS_EFT_AcctModifiedDate.visible(false);
        BankAccountSrc_SKS_EFT_AcctApprover.visible(false);
        BankAccountSrc_SKS_EFT_AcctApprovalDate.visible(false);
    }
    else
    {
        BankAccountSrc_SKS_EFT_AcctStatus.visible(true);
        BankAccountSrc_SKS_EFT_AcctModifier.visible(true);
        BankAccountSrc_SKS_EFT_AcctModifiedDate.visible(true);
        BankAccountSrc_SKS_EFT_AcctApprover.visible(true);
        BankAccountSrc_SKS_EFT_AcctApprovalDate.visible(true);
    }

  //  AttachmentPrompt.text("@SKS:SKGBankAttachName");
    IsVisibleValue.text("@SKS2850");
}

