void loadSKS_EFT_FileFormat()
{
    ;

    if(SKS_EFTCNP_Bank.PaymMode != custpaymmodetable.PaymMode)
    {
        select SKS_CNP_FormatId from custpaymmodetable
            where custpaymmodetable.PaymMode == SKS_EFTCNP_Bank.PaymMode
        join
            sks_eft_fileFormat where sks_eft_fileFormat.FormatId == custpaymmodetable.SKS_CNP_FormatId;
    }
}

