void setMaskAndLengthValues()
{
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspec;
    SKS_EFT_fileformatpaymspec sks_eft_fileformatpaymspecCpny;
    Int   CpnyNM;
    ;

    if(custpaymmodetable && sks_eft_fileformat)
    {
        //Company Info
        CpnyNm = 0;
        Select firstonly FormatId from sks_eft_fileformatpaymspec
            where sks_eft_fileformatpaymspec.formatid == custpaymmodetable.SKS_CNP_FormatId && sks_eft_Fileformatpaymspec.TxnNacha == NoYes::Yes &&
            sks_eft_Fileformatpaymspec.Selected == NoYes::Yes;
        Select firstonly FormatId from sks_eft_fileformatpaymspecCpny
            where sks_eft_fileformatpaymspecCpny.formatid == custpaymmodetable.SKS_CNP_FormatId && sks_eft_fileformatpaymspecCpny.CpnyIDNameReq == NoYes::Yes &&
            sks_eft_fileformatpaymspecCpny.Selected == NoYes::Yes;

        //Display CompanyID
        If(sks_eft_fileformat.CompanyIdTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFTCNP_Bank_ACHCompanyID.label(sks_eft_fileformat.CompanyIdTitle);
            element.setProperties(sks_eft_fileformat.CompanyIdMask, SKS_EFTCNP_Bank_ACHCompanyID);
            SKS_EFTCNP_Bank_ACHCompanyID.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFTCNP_Bank_ACHCompanyID.visible(False);
        }
        //Display Company Name
        IF(sks_eft_fileformat.CompanyNameTitle && (sks_eft_fileformatpaymspec || sks_eft_fileformatpaymspecCpny))
        {
            SKS_EFTCNP_Bank_ACHCompanyName.label(sks_eft_fileformat.CompanyNameTitle);
            element.setProperties(sks_eft_fileformat.CompanyNameMask, SKS_EFTCNP_Bank_ACHCompanyName);
            SKS_EFTCNP_Bank_ACHCompanyName.visible(True);
            CpnyNM = 1;
        }
        else
        {
            SKS_EFTCNP_Bank_ACHCompanyName.visible(False);
        }

        If (!CpnyNm)
        {
            SKS_EFTCNP_Bank_CompanyIDUnique.visible(False);
            SKS_EFTCNP_Bank_Companyidppd.visible(false);
            SKS_EFTCNP_Bank_CompanyNamePPD.visible(false);
            SKS_EFTCNP_Bank_CompanyidCCD.visible(false);
            SKS_EFTCNP_Bank_CompanyNameCCD.visible(false);
            SKS_EFTCNP_Bank_CompanyIDIAT.visible(false);
            SKS_EFTCNP_Bank_CompanyNameIAT.visible(false);
            SKS_EFTCNP_Bank_CompanyNameCTX.visible(false);
            SKS_EFTCNP_Bank_CompanyIDCTX.visible(false);

        }
        else
        {
            SKS_EFTCNP_Bank_CompanyIDUnique.visible(True);
            SKS_EFTCNP_Bank_CompanyIdUnique.visible(true);
            SKS_EFTCNP_Bank_Companyidppd.visible(true);
            SKS_EFTCNP_Bank_CompanyNamePPD.visible(true);
            SKS_EFTCNP_Bank_CompanyidCCD.visible(true);
            SKS_EFTCNP_Bank_CompanyNameCCD.visible(true);
            SKS_EFTCNP_Bank_CompanyIDIAT.visible(true);
            SKS_EFTCNP_Bank_CompanyNameIAT.visible(true);
            SKS_EFTCNP_Bank_CompanyNameCTX.visible(true);
            SKS_EFTCNP_Bank_CompanyIDCTX.visible(true);

            If(SKS_EFTCNP_Bank.CompanyIDUnique==True)
            {
                SKS_EFTCNP_Bank_CompanyIDPPD.enabled(True);
                SKS_EFTCNP_Bank_CompanyIDCCD.enabled(True);
                SKS_EFTCNP_Bank_CompanyIDIAT.enabled(True);
                SKS_EFTCNP_Bank_CompanyIDCTX.enabled(True);
                SKS_EFTCNP_Bank_CompanyNamePPD.enabled(True);
                SKS_EFTCNP_Bank_CompanyNameCCD.enabled(True);
                SKS_EFTCNP_Bank_CompanyNameIAT.enabled(True);
                SKS_EFTCNP_Bank_CompanyNameCTX.enabled(True);
            }
            else
            {
                SKS_EFTCNP_Bank_CompanyIDPPD.enabled(False);
                SKS_EFTCNP_Bank_CompanyIDCCD.enabled(False);
                SKS_EFTCNP_Bank_CompanyIDIAT.enabled(False);
                SKS_EFTCNP_Bank_CompanyIDCTX.enabled(False);
                SKS_EFTCNP_Bank_CompanyNamePPD.enabled(False);
                SKS_EFTCNP_Bank_CompanyNameCCD.enabled(False);
                SKS_EFTCNP_Bank_CompanyNameIAT.enabled(False);
                SKS_EFTCNP_Bank_CompanyNameCTX.enabled(False);
            }
        }

        if(sks_eft_fileformat.ImmDestNameTitle)
        {
            //Immediate destination
            SKS_EFTCNP_Bank_ACHImmDest.label(sks_eft_fileformat.ImmDestTitle);
            element.setProperties(sks_eft_fileformat.ImmDestMask, SKS_EFTCNP_Bank_ACHImmDest);

            SKS_EFTCNP_Bank_ACHImmDestName.label(sks_eft_fileformat.ImmDestNameTitle);
            element.setProperties(sks_eft_fileformat.ImmDestNameMask, SKS_EFTCNP_Bank_ACHImmDestName);
            SKS_EFTCNP_Bank_ACHImmDestName.visible(True);
        }
        else
        {
            SKS_EFTCNP_Bank_ACHImmDestName.visible(False);
        }

        IF(sks_eft_fileformat.ImmOriginNameTitle)
        {
            //Immediate originator
            SKS_EFTCNP_Bank_ACHImmOrig.label(sks_eft_fileformat.ImmOriginTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginMask, SKS_EFTCNP_Bank_ACHImmOrig);

            SKS_EFTCNP_Bank_ACHImmOrigName.label(sks_eft_fileformat.ImmOriginNameTitle);
            element.setProperties(sks_eft_fileformat.ImmOriginNameMask, SKS_EFTCNP_Bank_ACHImmOrigName);
            SKS_EFTCNP_Bank_ACHImmOrigName.visible(True);
        }
        else
        {
            SKS_EFTCNP_Bank_ACHImmOrigName.visible(False);
        }

        /*
        //Wire Plus values
        SKS_EFTCNP_Bank_W01.label(sks_eft_fileformat.WBank01Description);
        element.setProperties(sks_eft_fileformat.WBank01Mask, SKS_EFTCNP_Bank_W01);

        SKS_EFTCNP_Bank_W02.label(sks_eft_fileformat.WBank02Description);
        element.setProperties(sks_eft_fileformat.WBank02Mask, SKS_EFTCNP_Bank_W02);

        SKS_EFTCNP_Bank_W03.label(sks_eft_fileformat.WBank03Description);
        element.setProperties(sks_eft_fileformat.WBank03Mask, SKS_EFTCNP_Bank_W03);

        SKS_EFTCNP_Bank_W04.label(sks_eft_fileformat.WBank04Description);
        element.setProperties(sks_eft_fileformat.WBank04Mask, SKS_EFTCNP_Bank_W04);*/
    }
    else
    {
        element.setProperties('', SKS_EFTCNP_Bank_W01);
        element.setProperties('', SKS_EFTCNP_Bank_W02);
        element.setProperties('', SKS_EFTCNP_Bank_W03);
        element.setProperties('', SKS_EFTCNP_Bank_W04);
    }
}

void setProperties(str _maskValue, FormStringControl _formControl)
{
    ;

    if(_maskValue)
    {
        _formControl.visible(true);

        if (substr(_maskValue, 0, 1) == 'U')
        {
            _formControl.changeCase(2);
        }

        _formControl.limitText(strLen(_maskValue));
    }
    else
    {
        _formControl.visible(false);
        _formControl.changeCase(0);
        _formControl.limitText(0, AutoMode::Auto);
    }
}

void SKS_setEFTTab()
{
    if(sks_eft_setup.APAcctApproval)
    {
        IsVisibleValue.visible(false);
    }
    else
    {
        IsVisibleValue.visible(true);
    }
}

/// <summary>
