}

/// <summary>
/// Method used to disable internal contact. SKS_AP_remit attachment
/// </summary>
// SKS_AP_remit attachment
void sks_APdisableInternalContact()
{
    boolean enabledProperty;
    ;

    if (SKS_EFTCNP_Bank.CNP_EMFromSetup == NoYes::Yes)
    {
        enabledProperty = boolean::false;
    }
    else
    {
        enabledProperty = boolean::true;
    }
    SKS_EFTCNP_Bank_CNPContactName.enabled(enabledProperty);
    SKS_EFTCNP_Bank_CNPContactPhone.enabled(enabledProperty);
    SKS_EFTCNP_Bank_CNPContactEmail.enabled(enabledProperty);
}

void loadReversingPaymentfromSetup()
{
    SKS_EFTCNP_Bank.AllowPostedPmtReversal = sks_eft_setup.CNPAllowPostedPmtJournal;
    SKS_EFTCNP_Bank.RevJournalName = sks_eft_setup.CNPRevJournalName;
    SKS_EFTCNP_Bank.RevPaymMode = sks_eft_setup.CNPRevPaymMode;
    SKS_EFTCNP_Bank.AutoPostPaymentJournal = sks_eft_setup.CNPAutoPostJournal;
}

void disablePostedPaymInfo()
{
    if(SKS_EFTCNP_Bank.PostedPaymSetupDefaults == NoYes::Yes)
    {
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, AllowPostedPmtReversal)).allowedit(false);
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, RevJournalName)).allowedit(false);
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, RevPaymMode)).allowedit(false);
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, AutoPostPaymentJournal)).allowedit(false);
    }
    else
    {
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, AllowPostedPmtReversal)).allowedit(true);
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, RevJournalName)).allowedit(true);
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, RevPaymMode)).allowedit(true);
        SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, AutoPostPaymentJournal)).allowedit(true);
    }
}

void BankFabricTableLookup(Args _args, SKS_BF_FileLocCategory _category,SKS_BF_LookupTable _lookuptable)
{
    SKS_BF_TmpLookupTable TmpFileLocations;
    Query query = new Query();

    QueryBuildDataSource qbds;

    QueryBuildRange queryBuildRange;

    SysTableLookup sysTableLookup;

    TmpFileLocations = SKS_BF_TmpLookupTable::construct(_category,_lookuptable);

    sysTableLookup = SysTableLookup::newParameters(tablenum(SKS_BF_TmpLookupTable),_args.parmObject());

    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Name));
    sysTableLookup.addLookupfield(fieldnum(SKS_BF_TmpLookupTable,Description));

    qbds = query.addDataSource(tableNum(SKS_BF_TmpLookupTable));

    queryBuildRange = qbds.addRange(fieldnum(SKS_BF_TmpLookupTable,Name));

    sysTableLookup.parmTmpBuffer(TmpFileLocations);

    sysTableLookup.performFormLookup();
}

