public boolean validateWrite()
{
    boolean ret;
    SKS_EB_FileStorage  sks_EB_FileStorage;
    ;

    //BankFabric
    select firstonly sks_EB_FileStorage;

    if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
    {
        if(!SKS_EFTCNP_Bank_CNPBankFabricFileLocation.valueStr())
        {
            warning(strFmt("@SKS2643","@SKS:SKGBFCNPFileLocation")); //required field

            SKS_EFTCNP_Bank_CNPBankFabricFileLocation.setFocus();

            return false;
        }
    }
    else
    {
        if(!SKS_EFTCNP_Bank_CNPFileName.valueStr())
        {
            warning(strFmt("@SKS2643",'File name')); //required field

            return false;
        }

        if(!SKS_EFTCNP_Bank_CNPFilePath.valueStr())
        {
            warning(strFmt("@SKS2643",'File path')); //required field

            return false;
        }
        if(SKS_EFTCNP_Bank.CNPArchive && !SKS_EFTCNP_Bank.CNPPathArchive && sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::FileHubLocalEnumValue)
        {
            warning(strFmt("@SKS2643","@SKS75")); //required field

            return false;
        }
    }

    if(SKS_EFTCNP_Bank.CNPCommID == '')
    {
        warning(strFmt("@SKS2643","@SKS2444")); //required field

        return false;
    }

    if(SKS_EFTCNP_Bank.InboundCommID != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFTCNP_Bank.CNPBankFabricInboundFileLocation == '')
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFInbFileLocation"));

                return false;
            }
        }
        else
        {
            if(SKS_EFTCNP_Bank.CNPInboundFileLoc == '')
            {
                warning(strFmt("@SKS2643","@SKS2716"));

                return false;
            }
        }
    }

    if(SKS_EFTCNP_Bank.CNPControlFileFormatiD != '')
    {
        if (sks_EB_FileStorage.StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
        {
            if(SKS_EFTCNP_Bank.CNPBankFabricFileNames == '')
            {
                warning(strFmt("@SKS2643","@SKS:SKGBFFileNames")); //required field

                return false;
            }
        }
        else
        {
            if(SKS_EFTCNP_Bank.CNPControlFileName == '')
            {
                warning(strFmt("@SKS2643","@SKS3242")); //required field

                return false;
            }
        }
    }

    ret = super();
    ret = SKS_EFTCNP_Bank.SKS_DD_FileLocationValide(SKS_EFTCNP_Bank);

    //SKS_FDD_16447/16448--->
    if(ret)
    {
        SKS_EFT_FileFormatFields             lSKS_EFT_FileFormatFields;
        SKS_EFT_CNPBankAccountPaymLookup     lSKS_EFT_BankAccountPaymLookup;

        while select required from lSKS_EFT_FileFormatFields join lSKS_EFT_BankAccountPaymLookup
            where lSKS_EFT_BankAccountPaymLookup.FileFormatFieldsRecId == lSKS_EFT_FileFormatFields.recid &&
            lSKS_EFT_BankAccountPaymLookup.BankPaymRecId == SKS_EFTCNP_Bank.RecId
            && lSKS_EFT_FileFormatFields.Required == NoYes::Yes
        {
            if(lSKS_EFT_BankAccountPaymLookup.fixedvalue == '' && lSKS_EFT_BankAccountPaymLookup.CodeLookupValuesRecId == 0)
            {
                error("@SKS2682");
                return false;
            }
        }
    }
    //SKS_FDD_16447/16448<---

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.disablePostedPaymInfo();            
    element.loadSKS_EFT_FileFormat();
    element.disableCompanyInfo();
    element.disableFileHeader();
    element.disableFileInfo();
    element.sks_APdisableInternalContact();
    //element.disableWireIAT();

    element.setMaskAndLengthValues();
    SKS_EFTCNP_Bank_ds.object(fieldnum(SKS_EFTCNP_Bank, CNPPathARchive)).enabled(SKS_EFTCNP_Bank.CNPArchive);
    element.enableDisableInboundInfo();
    element.enableDisableControlFile();

    if(SKS_EFTCNP_Bank.CompanyFromSetup == NoYes::Yes)
    {
        element.loadCompanyInfoFromSetup();
    }

    if(SKS_EFTCNP_Bank.CNPFileFromSetup == NoYes::Yes)
    {
        element.loadFileInfoFromSetup();
    }

    return ret;
}

