    ret = super();

    if(ret)
    {
        ret = SKS_EB_FileHub::directoryExistsForm(SKS_EFTCNP_Bank.CNPInboundFileLoc);
    }
    if(ret)
    {
        ret = SKS_EFTCNP_Bank.SKS_DD_FileLocationValide(SKS_EFTCNP_Bank) && ret;
    }

    return ret;
}

