public int active()
{
    int ret;

    ret = super();

    if(SKS_EFT_FileFormatFields.FieldType == SKS_EFT_CodeFieldType::CodeLookup)
    {
        SKS_EFT_FileFormatFields_FixedValue.enabled(false);
        Referencegrouplookup.enabled(true);
    }
    else
    {
        SKS_EFT_FileFormatFields_FixedValue.enabled(true);
        SKS_EFT_FileFormatFields_FixedValue.limitText(strLen(strRTrim(SKS_EFT_FileFormatFields.fixedmask)), AutoMode::Fixed);//SKS#DevOps:15839
        Referencegrouplookup.enabled(false);
    }

    return ret;
}

