void enableDisable()
{    
    SKS_EFT_InboundFileCompletion lSKS_EFT_InboundFileCompletion;
    if (SKS_EFT_InboundFile.APIFormatType == SKS_EFT_APIFormatType::File)
    {            
        if(SKS_EFT_InboundFile.RecId == 0)
        {
            ErrorLog.enabled(false);
            InboundFile.enabled(false);
        }
        else
        {
            if(SKS_EFT_InboundFile.FileError)
                ErrorLog.enabled(true);
            else
                ErrorLog.enabled(false);

            SKS_BF_BankIntType sks_BF_BankIntType;

            if (SKS_EB_FileStorage::find().StorageOption_APEFTFXDD == SKS_EB_BankIntegOptWithNoLicense::BankFabricEnumValue)
            {
                sks_BF_BankIntType = SKS_BF_BankIntType::BankFabric;
            }
            else
            {
                sks_BF_BankIntType = SKS_BF_BankIntType::FileHubLocal;
            }

            SKS_EB_FileHub fileHub = SKS_EB_FileHub::construct(sks_BF_BankIntType);
            fileHub.runFileInquire(SKS_EFT_InboundFile.FilePathName);
            if(fileHub.attrFileExists())
            {
                InboundFile.enabled(true);
            }
            else
            {
                InboundFile.enabled(false);
            }
        }
    }

    if(sks_eft_inboundfile.outboundfilerecid != 0)
    {
        sks_eft_inboundfile_ds.object(fieldnum(sks_eft_inboundFile,Outboundfilerecid)).allowedit(false);
    }
    else
    {
        sks_eft_inboundfile_ds.object(fieldnum(sks_eft_inboundFile,Outboundfilerecid)).allowedit(true);
    }
    if (SKS_EFT_InboundFile)
    {
        select firstOnly lSKS_EFT_InboundFileCompletion
            where lSKS_EFT_InboundFileCompletion.InboundFileGroupId == SKS_EFT_InboundFile.InboundFileGroupId
                && lSKS_EFT_InboundFileCompletion.InboundStatusRecID == SKS_EFT_InboundFile.InboundStatusesRecId;
        if (lSKS_EFT_InboundFileCompletion)
        {
            AddStatusAction.enabled(false);
        }
        else
        {
            AddStatusAction.enabled(true);
        }
    }
    else
    {
        AddStatusAction.enabled(false);
    }
}

public void init()
{
    super();
    this.enableDisable();
    if (element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_InboundFile)
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesThatNeedReviewMI))
    {
        this.design().caption("@SKS2738");             
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_DD_inboundfile) 
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasARDiretDebitInboudFiles_Review) ||
             element.getSKS_EFT_Module() == SKS_EFT_Module::AR)
    {
        this.design().caption("@SKS4146");
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_BTB_Inboundfile) ||
        element.getSKS_EFT_Module() == SKS_EFT_Module::BTB)
    {
        this.design().caption("@SKS:MenuItem_SKS_BankTransferInboundFile_Label");
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_CNP_Inboundfile)   ||
             element.getSKS_EFT_Module() == SKS_EFT_Module::CNP)
    {
        this.design().caption("@SKS:CNPInboundFiles");
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_APIInboundFile))
    {
        this.design().caption("@SKS:SKGAPIInbound");
        InboundFile.visible(false);
        ErrorLog.visible(false);
        SKS_EFT_APIInboundFileLine.visible(true);
        SKS_EFT_InboundFileLine.visible(false);

        FormStringControl fileNbr = this.Design().controlName(formControlStr(SKS_EFT_InboundFile, SKS_EFT_InboundFile_InboundFileNbr)) as FormStringControl;
        fileNbr.label('@SKS:SKGAPIInboundId');

        FormCheckBoxControl fileError = this.Design().controlName(formControlStr(SKS_EFT_InboundFile, SKS_EFT_InboundFile_FileError)) as FormCheckBoxControl;
        fileError.label('@SKS:SKGAPIError');

        FormStringControl formatId = this.Design().controlName(formControlStr(SKS_EFT_InboundFile, SKS_EFT_InboundFile_InboundFormatId)) as FormStringControl;
        formatId.label('@SKS:SKGAPIInboundAPI');

        FormReferenceGroupControl outBoundId = this.Design().controlName(formControlStr(SKS_EFT_InboundFile, ReferenceGroupOutboundFileRecId)) as FormReferenceGroupControl;
        outBoundId.label('@SKS:SKGAPIOutBoundId');

        FormStringControl inBoundFileGrpId = this.Design().controlName(formControlStr(SKS_EFT_InboundFile, AdditonalData_InboundFileGroupId)) as FormStringControl;
        inBoundFileGrpId.label('@SKS:SKGAPIInboundGroupId');

        SKS_EFT_InboundFile_ds.object(fieldNum(SKS_EFT_InboundFile, FilePathName)).visible(false);
        SKS_EFT_InboundFile_ds.object(fieldNum(SKS_EFT_InboundFile, FileDateTime)).visible(false);
    }


}

