public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;

    queryBuildDataSource = this.query().dataSourceName(SKS_EFT_InboundFile_ds.name());
    queryBuildDataSource.clearRanges();

    if (element.args().menuItemName() == menuitemDisplayStr(SKS_DD_inboundfile)
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasARDiretDebitInboudFiles_Review))
    {
        //adding query range for direct debit
        queryBuildDataSource.addRange(fieldnum(SKS_EFT_InboundFile, Module)).value(queryValue(SKS_EFT_Module::AR));
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_InboundFile)
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesThatNeedReviewMI)
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_APIInboundFile)
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesErrorsMI)
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesUnmatchedMI))
    {
        //adding query range for AP
        queryBuildDataSource.addRange(fieldnum(SKS_EFT_InboundFile, Module)).value(queryValue(SKS_EFT_Module::AP));
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_BTB_Inboundfile))
    {
        //adding query range for BTB
        queryBuildDataSource.addRange(fieldnum(SKS_EFT_InboundFile, Module)).value(queryValue(SKS_EFT_Module::BTB));
    }
    else if (element.args().menuItemName() == menuitemDisplayStr(SKS_CNP_Inboundfile))
    {
        //adding query range for Customer negative payment
        queryBuildDataSource.addRange(fieldnum(SKS_EFT_InboundFile, Module)).value(queryValue(SKS_EFT_Module::CNP));
    }
    queryBuildDataSource.addSortField(fieldNum(SKS_EFT_InboundFile, InboundFileNbr), SortOrder::Descending);

    //If workspace tile's MenuItem, we only disply the items for review                  
    if(element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesThatNeedReviewMI) 
        || element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasARDiretDebitInboudFiles_Review))
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFile,TransactionStatus)).value(queryValue(SKS_EFT_InboundTransactionStatus::NeedsReviewed));

        if(element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesThatNeedReviewMI))
        {   
            QueryBuildDataSource qbds_sks_eftap_file = queryBuildDataSource.addDataSource(tableNum(SKS_EFTAP_File));
            qbds_sks_eftap_file.joinMode(JoinMode::InnerJoin);
            qbds_sks_eftap_file.fetchMode(QueryFetchMode::One2One);
            qbds_sks_eftap_file.addLink(fieldNum(SKS_EFT_InboundFile, OutboundFileRecId), fieldNum(SKS_EFTAP_File, RecId));                    
            qbds_sks_eftap_file.addRange(fieldNum(SKS_EFTAP_File, FileNbr)).value(SysQuery::valueNotEmptyString());
        }   
    }

    if(element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesErrorsMI))
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFile,FileError)).value(enum2str(NoYes::Yes));               
    }

    if(element.args().menuItemName() == menuitemDisplayStr(SKS_EFT_TasAPInboundFilesUnmatchedMI))
    {
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFile,TransactionStatus)).value(SysQuery::valueNot(SKS_EFT_InboundTransactionStatus::NeedsReviewed));
        queryBuildDataSource.addRange(fieldNum(SKS_EFT_InboundFile,FileError)).value(enum2str(NoYes::No));
    }

    if (element.args().menuItemName() == menuItemDisplayStr(SKS_EFT_APIInboundFile))
    {
        queryBuildDataSource.addRange(fieldnum(SKS_EFT_InboundFile, APIFormatType)).value(queryValue(SKS_EFT_APIFormatType::API));
    }
    else
    {
        queryBuildDataSource.addRange(fieldnum(SKS_EFT_InboundFile, APIFormatType)).value(queryValue(SKS_EFT_APIFormatType::File));
    }

    super();
}

