public boolean validate()
{
    boolean ret;

    ret = super();

    if(ret &&
        !(SKS_EFT_InboundFile.TransactionStatus == SKS_EFT_InboundTransactionStatus::Reviewed ||
         SKS_EFT_InboundFile.TransactionStatus == SKS_EFT_InboundTransactionStatus::NeedsReviewed))
    {
        error("@SKS2907");
