[SuppressBPWarningAttribute('BPCheckSelectForUpdateAbsent','Buffer lsks_EFT_InboundFile is passed to other methods where it is subject to updates, thus it needs to be selected forupdate')]
void clicked()
{
    Dialog                      dialog;
    DialogField                 FileStatus;
    DialogField                 apply2All;
    SKS_EFTAP_File              lsks_EFTAP_File;
    SKS_EFT_FileDetail          lsks_EFT_FileDetail;
    SKS_EFT_InboundFile         lsks_EFT_InboundFile;
    SKS_EFT_InboundFileCompletion inboundFileCompletion;

    ;

    // Create Default Bank Account Information dialog
    dialog = new Dialog("@SKS3098");
    FileStatus = dialog.addField(enumStr(SKS_EB_FileStatusAction));
    apply2All = dialog.addField(enumStr(NoYes),strFmt("@SKS2915",SKS_EFT_InboundStatuses.StatusId));
    apply2All.value(NoYes::Yes);
    if (dialog.run())
    {
        ttsBegin;
        inboundFileCompletion.InboundFileGroupId = SKS_EFT_InboundFile.InboundFileGroupId;
        inboundFileCompletion.InboundStatusRecID = SKS_EFT_InboundFile.InboundStatusesRecId;
        inboundFileCompletion.FileStatusAction = FileStatus.value();
        inboundFileCompletion.insert();
        if (apply2All)
        {
            while select forUpdate lsks_EFT_InboundFile
                    where lsks_EFT_InboundFile.InboundFileGroupId == SKS_EFT_InboundFile.InboundFileGroupId
                        && lsks_EFT_InboundFile.InboundStatusesRecId == SKS_EFT_InboundFile.InboundStatusesRecId
                        && lsks_EFT_InboundFile.OutboundFileRecID != 0
                    join lsks_EFTAP_File
                        where lsks_EFT_InboundFile.OutboundFileRecID == lsks_EFTAP_File.RecId
                    join lsks_EFT_FileDetail
                        where lsks_EFTAP_File.RecId == lsks_EFT_FileDetail.FileRecId
            {
                SKS_EFT_InboundFiles::findStatus(lSKS_EFT_InboundFile);
            }
        }
        ttsCommit;
        SKS_EFT_InboundFile_ds.research(true);
    }
    return;
}

public Common lookupReference()
{
    SKS_EFTAP_File   lSKS_EFTAP_File;
    Query                   query = new Query();
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    // Create an instance of SysTableLookup where 'this' is the current form control
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(SKS_EFTAP_File), ReferenceGroupOutboundFileRecId, true);
    ;

    // Add fields to be shown in the lookup form
    sysTableLookup.addLookupField(fieldNum(SKS_EFTAP_File, FileNbr));
    sysTableLookup.addLookupField(fieldNum(SKS_EFTAP_File, FileStatus));

    // Limit and arrange the data selection
    queryBuildDataSource = query.addDataSource(tableNum(SKS_EFTAP_File));
    queryBuildDataSource.addSortField(fieldNum(SKS_EFTAP_File, FileNbr), SortOrder::Descending);
    queryBuildRange = queryBuildDataSource.addRange(fieldNum(SKS_EFTAP_File, EFTFileType));
    //VSTS_11602 -->
    if(SKS_EFT_InboundFile.Module == SKS_EFT_Module::AP)
    {
        queryBuildRange.value(int2str(SKS_EFT_Module::AP));
    }
    else if(SKS_EFT_InboundFile.Module == SKS_EFT_Module::BTB)
    {
        queryBuildRange.value(int2str(SKS_EFT_Module::BTB));
    }
    else if(SKS_EFT_InboundFile.Module == SKS_EFT_Module::CNP)
    {
        queryBuildRange.value(int2str(SKS_EFT_Module::CNP));
    }
    else
    {
        queryBuildRange.value(int2str(SKS_EFT_Module::AR));
    }
    //VSTS_11602 <--
    sysTableLookup.parmQuery(query);

    // Perform the lookup
    lSKS_EFTAP_File = sysTableLookup.performFormLookup();
    return lSKS_EFTAP_File;
}

public void jumpRef()
{
    SKS_EFTAP_File  sksEfTapFile;
    SKS_EFT_Module  sksEftModule;


    //super();
    // BugId: 7237
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(SKS_EFTAP_CreateSendFiles), MenuItemType::Display);
    Args         args = new args();

    select firstonly sksEfTapFile
        where sksEfTapFile.RecId == SKS_EFT_InboundFile.OutboundFileRecID;

    sksEftModule = SKS_EFT_InboundFile.Module;

    args.caller(this.formRun());
    args.parmEnum(sksEftModule);
    args.parmEnumType(enumNum(SKS_EFT_Module));
    args.lookupRecord(sksEfTapFile);
    menuFunction.run(args);

}
