    }

    if (this.args().menuItemName() == menuItemDisplayStr(SKS_EFT_APIInboundFileGroups))
    {
        this.design().caption("@SKS:SKGAPIInboundGroups");

        SKS_EFT_InboundFileGroups_ds.object(fieldNum(SKS_EFT_InboundFileGroups, InboundFormatId2)).visible(false);
